import React, { useState, useEffect } from 'react';
import { getBills, addBill, toggleBillStatus, deleteBill } from '../services/api';

const Bills = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newBill, setNewBill] = useState({
        description: '',
        amount: '',
        dueDate: '',
        category: 'Bills & Utilities'
    });

    useEffect(() => {
        fetchBills();
    }, []);

    const fetchBills = async () => {
        try {
            const response = await getBills();
            setBills(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Failed to fetch bills', error);
            setLoading(false);
        }
    };

    const handleAddBill = async (e) => {
        e.preventDefault();
        try {
            await addBill(newBill);
            setShowAddModal(false);
            setNewBill({ description: '', amount: '', dueDate: '', category: 'Bills & Utilities' });
            fetchBills();
        } catch (error) {
            console.error('Failed to add bill', error);
        }
    };

    const handleToggleStatus = async (id) => {
        try {
            await toggleBillStatus(id);
            fetchBills();
        } catch (error) {
            console.error('Failed to toggle bill status', error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this bill?')) {
            try {
                await deleteBill(id);
                fetchBills();
            } catch (error) {
                console.error('Failed to delete bill', error);
            }
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: 'numeric' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    const getDaysDue = (dueDate) => {
        const today = new Date();
        const due = new Date(dueDate);
        const diffTime = due - today;
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays < 0) return `Overdue by ${Math.abs(diffDays)} days`;
        if (diffDays === 0) return 'Due today';
        return `Due in ${diffDays} days`;
    };

    return (
        <div className="dashboard">
            <div className="dashboard-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div className="header-content">
                    <h1>Bills</h1>
                    <p>Manage and track your recurring bills</p>
                </div>
                <button
                    className="add-transaction-btn"
                    onClick={() => setShowAddModal(true)}
                >
                    + Add Bill
                </button>
            </div>

            {showAddModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Add New Bill</h3>
                        <form onSubmit={handleAddBill}>
                            <div className="form-group">
                                <label>Description</label>
                                <input
                                    type="text"
                                    value={newBill.description}
                                    onChange={(e) => setNewBill({ ...newBill, description: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Amount</label>
                                <input
                                    type="number"
                                    value={newBill.amount}
                                    onChange={(e) => setNewBill({ ...newBill, amount: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Due Date</label>
                                <input
                                    type="date"
                                    value={newBill.dueDate}
                                    onChange={(e) => setNewBill({ ...newBill, dueDate: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Category</label>
                                <select
                                    value={newBill.category}
                                    onChange={(e) => setNewBill({ ...newBill, category: e.target.value })}
                                >
                                    <option>Bills & Utilities</option>
                                    <option>Rent</option>
                                    <option>Subscription</option>
                                    <option>Insurance</option>
                                    <option>Loan</option>
                                    <option>Other</option>
                                </select>
                            </div>
                            <div className="modal-actions">
                                <button type="button" onClick={() => setShowAddModal(false)}>Cancel</button>
                                <button type="submit" className="submit-btn">Add Bill</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            <div className="bills-list-container" style={{
                background: 'var(--card-bg)',
                borderRadius: '16px',
                padding: '2rem',
                boxShadow: 'var(--card-shadow)'
            }}>
                {loading ? (
                    <p>Loading bills...</p>
                ) : bills.length === 0 ? (
                    <div style={{ textAlign: 'center', color: 'var(--text-secondary)' }}>
                        <p>No bills found. Add one to get started!</p>
                    </div>
                ) : (
                    <div className="bills-grid" style={{ display: 'grid', gap: '1rem' }}>
                        {bills.map(bill => (
                            <div key={bill.id} className="bill-item" style={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                padding: '1rem',
                                border: '1px solid var(--border-color)',
                                borderRadius: '12px',
                                background: bill.isPaid ? 'rgba(16, 185, 129, 0.1)' : 'transparent',
                                opacity: bill.isPaid ? 0.7 : 1
                            }}>
                                <div className="bill-info">
                                    <h3 style={{ margin: '0 0 0.25rem 0', fontSize: '1.1rem' }}>{bill.description}</h3>
                                    <div style={{ display: 'flex', gap: '1rem', color: 'var(--text-secondary)', fontSize: '0.9rem' }}>
                                        <span>{bill.category}</span>
                                        <span>•</span>
                                        <span style={{
                                            color: !bill.isPaid && new Date(bill.dueDate) < new Date() ? '#EF4444' : 'inherit',
                                            fontWeight: !bill.isPaid ? '600' : 'normal'
                                        }}>
                                            {formatDate(bill.dueDate)} ({getDaysDue(bill.dueDate)})
                                        </span>
                                    </div>
                                </div>
                                <div className="bill-actions" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
                                    <div className="bill-amount" style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>
                                        ${bill.amount.toFixed(2)}
                                    </div>
                                    <button
                                        onClick={() => handleToggleStatus(bill.id)}
                                        style={{
                                            padding: '0.5rem 1rem',
                                            borderRadius: '8px',
                                            border: 'none',
                                            background: bill.isPaid ? '#10B981' : '#E5E7EB',
                                            color: bill.isPaid ? 'white' : '#374151',
                                            cursor: 'pointer',
                                            fontWeight: '600'
                                        }}
                                    >
                                        {bill.isPaid ? 'Paid' : 'Mark Paid'}
                                    </button>
                                    <button
                                        onClick={() => handleDelete(bill.id)}
                                        style={{
                                            border: 'none',
                                            background: 'transparent',
                                            color: '#EF4444',
                                            cursor: 'pointer',
                                            fontSize: '1.2rem'
                                        }}
                                        title="Delete Bill"
                                    >
                                        ×
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Bills;
