sourcesContent:
import React, { useState, useEffect } from 'react';
import {
    FiUser, FiLock, FiBell, FiCreditCard,
    FiDollarSign, FiGlobe, FiMoon, FiSun,
    FiSave, FiUpload, FiDownload, FiTrash2
} from 'react-icons/fi';
import { toast } from 'react-toastify';

const Settings = ({ isDarkMode, toggleTheme }) => {
    const [userProfile, setUserProfile] = useState({
        name: 'Alex Johnson',
        email: 'alex.johnson@example.com',
        currency: 'USD',
        language: 'en',
        notifications: true,
        twoFactorAuth: false,
        autoBackup: true,
        budgetAlerts: true,
        spendingLimits: true
    });

    const [backupFrequency, setBackupFrequency] = useState('weekly');
    const [dataRetention, setDataRetention] = useState('12');
    const [exportFormat, setExportFormat] = useState('csv');
    const [isSaving, setIsSaving] = useState(false);
    const [activeTab, setActiveTab] = useState('general');

    useEffect(() => {
        // Load saved settings from localStorage
        const savedSettings = localStorage.getItem('moneyManagerSettings');
        if (savedSettings) {
            setUserProfile(prev => ({
                ...prev,
                ...JSON.parse(savedSettings)
            }));
        }

        const savedFrequency = localStorage.getItem('backupFrequency');
        if (savedFrequency) {
            setBackupFrequency(savedFrequency);
        }

        const savedRetention = localStorage.getItem('dataRetention');
        if (savedRetention) {
            setDataRetention(savedRetention);
        }
    }, []);

    const handleProfileChange = (field, value) => {
        setUserProfile(prev => ({
            ...prev,
            [field]: value
        }));
    };

    const handleSaveSettings = async () => {
        setIsSaving(true);
        try {
            // Save to localStorage
            localStorage.setItem('moneyManagerSettings', JSON.stringify(userProfile));
            localStorage.setItem('backupFrequency', backupFrequency);
            localStorage.setItem('dataRetention', dataRetention);

            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1000));

            toast.success('Settings saved successfully!', {
                position: "top-right",
                autoClose: 3000,
            });
        } catch (error) {
            toast.error('Failed to save settings', {
                position: "top-right",
                autoClose: 3000,
            });
        } finally {
            setIsSaving(false);
        }
    };

    const handleExportData = () => {
        const data = {
            settings: userProfile,
            backupFrequency,
            dataRetention,
            exportFormat,
            exportDate: new Date().toISOString()
        };

        const dataStr = JSON.stringify(data, null, 2);
        const dataBlob = new Blob([dataStr], { type: 'application/json' });
        const url = URL.createObjectURL(dataBlob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `money-manager-settings-${new Date().toISOString().split('T')[0]}.json`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);

        toast.info('Settings exported successfully!', {
            position: "top-right",
            autoClose: 3000,
        });
    };

    const handleImportData = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const importedData = JSON.parse(e.target.result);

                if (importedData.settings) {
                    setUserProfile(prev => ({
                        ...prev,
                        ...importedData.settings
                    }));
                }

                if (importedData.backupFrequency) {
                    setBackupFrequency(importedData.backupFrequency);
                }

                if (importedData.dataRetention) {
                    setDataRetention(importedData.dataRetention);
                }

                toast.success('Settings imported successfully!', {
                    position: "top-right",
                    autoClose: 3000,
                });
            } catch (error) {
                toast.error('Invalid settings file', {
                    position: "top-right",
                    autoClose: 3000,
                });
            }
        };
        reader.readAsText(file);
    };

    const handleResetSettings = () => {
        if (window.confirm('Are you sure you want to reset all settings to default?')) {
            setUserProfile({
                name: 'Alex Johnson',
                email: 'alex.johnson@example.com',
                currency: 'USD',
                language: 'en',
                notifications: true,
                twoFactorAuth: false,
                autoBackup: true,
                budgetAlerts: true,
                spendingLimits: true
            });
            setBackupFrequency('weekly');
            setDataRetention('12');
            setExportFormat('csv');

            localStorage.removeItem('moneyManagerSettings');
            localStorage.removeItem('backupFrequency');
            localStorage.removeItem('dataRetention');

            toast.info('Settings reset to default', {
                position: "top-right",
                autoClose: 3000,
            });
        }
    };

    const tabs = [
        { id: 'general', label: 'General', icon: <FiUser /> },
        { id: 'security', label: 'Security', icon: <FiLock /> },
        { id: 'notifications', label: 'Notifications', icon: <FiBell /> },
        { id: 'data', label: 'Data & Backup', icon: <FiUpload /> }
    ];

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <div className="header-content">
                    <h1>Settings</h1>
                    <p>Manage your account preferences and settings</p>
                </div>
                <div className="header-actions">
                    <button
                        className="btn-secondary"
                        onClick={handleResetSettings}
                        style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                    >
                        <FiTrash2 /> Reset All
                    </button>
                    <button
                        className="btn-primary"
                        onClick={handleSaveSettings}
                        disabled={isSaving}
                        style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                    >
                        {isSaving ? (
                            <>
                                <div className="spinner"></div>
                                Saving...
                            </>
                        ) : (
                            <>
                                <FiSave /> Save Changes
                            </>
                        )}
                    </button>
                </div>
            </div>

            {/* Tabs */}
            <div style={{
                display: 'flex',
                gap: '0.5rem',
                marginBottom: '2rem',
                paddingBottom: '0.5rem',
                borderBottom: '2px solid var(--border-color)',
                overflowX: 'auto'
            }}>
                {tabs.map(tab => (
                    <button
                        key={tab.id}
                        onClick={() => setActiveTab(tab.id)}
                        style={{
                            padding: '0.75rem 1.5rem',
                            background: activeTab === tab.id ? 'var(--primary-color)' : 'transparent',
                            color: activeTab === tab.id ? 'white' : 'var(--text-color)',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            fontWeight: '600',
                            fontSize: '0.95rem',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.5rem',
                            whiteSpace: 'nowrap',
                            transition: 'all 0.2s'
                        }}
                    >
                        {tab.icon}
                        {tab.label}
                    </button>
                ))}
            </div>

            {/* General Settings */}
            {activeTab === 'general' && (
                <div className="settings-section">
                    <div className="section-header">
                        <FiUser size={24} />
                        <h2>General Settings</h2>
                    </div>

                    <div className="settings-grid">
                        <div className="form-group">
                            <label>Full Name</label>
                            <input
                                type="text"
                                value={userProfile.name}
                                onChange={(e) => handleProfileChange('name', e.target.value)}
                                placeholder="Enter your full name"
                            />
                        </div>

                        <div className="form-group">
                            <label>Email Address</label>
                            <input
                                type="email"
                                value={userProfile.email}
                                onChange={(e) => handleProfileChange('email', e.target.value)}
                                placeholder="Enter your email"
                            />
                        </div>

                        <div className="form-group">
                            <label>Default Currency</label>
                            <select
                                value={userProfile.currency}
                                onChange={(e) => handleProfileChange('currency', e.target.value)}
                            >
                                <option value="USD">USD ($)</option>
                                <option value="EUR">EUR ()</option>
                                <option value="GBP">GBP ()</option>
                                <option value="JPY">JPY ()</option>
                                <option value="AUD">AUD (A$)</option>
                                <option value="CAD">CAD (C$)</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Language</label>
                            <select
                                value={userProfile.language}
                                onChange={(e) => handleProfileChange('language', e.target.value)}
                            >
                                <option value="en">English</option>
                                <option value="es">Spanish</option>
                                <option value="fr">French</option>
                                <option value="de">German</option>
                                <option value="zh">Chinese</option>
                                <option value="ja">Japanese</option>
                            </select>
                        </div>

                        <div className="form-group" style={{ gridColumn: '1 / -1' }}>
                            <label>Appearance</label>
                            <div style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '1rem',
                                padding: '1rem',
                                background: 'var(--light-color)',
                                borderRadius: '10px',
                                border: '1px solid var(--border-color)'
                            }}>
                                <div style={{
                                    width: '40px',
                                    height: '40px',
                                    borderRadius: '50%',
                                    background: 'var(--light-color)',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    color: isDarkMode ? '#F59E0B' : '#374151'
                                }}>
                                    {isDarkMode ? <FiMoon /> : <FiSun />}
                                </div>
                                <div style={{ flex: 1 }}>
                                    <div style={{ fontWeight: '600', color: 'var(--text-color)' }}>
                                        {isDarkMode ? 'Dark Mode' : 'Light Mode'}
                                    </div>
                                    <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                        {isDarkMode ? 'Dark theme is active' : 'Light theme is active'}
                                    </div>
                                </div>
                                <div className="switch">
                                    <input
                                        type="checkbox"
                                        checked={isDarkMode}
                                        onChange={toggleTheme}
                                    />
                                    <span className="slider"></span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Security Settings */}
            {activeTab === 'security' && (
                <div className="settings-section">
                    <div className="section-header">
                        <FiLock size={24} />
                        <h2>Security Settings</h2>
                    </div>

                    <div className="settings-grid">
                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Two-Factor Authentication</div>
                                <div className="setting-description">
                                    Add an extra layer of security to your account
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={userProfile.twoFactorAuth}
                                    onChange={(e) => handleProfileChange('twoFactorAuth', e.target.checked)}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Automatic Logout</div>
                                <div className="setting-description">
                                    Automatically logout after 30 minutes of inactivity
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={true}
                                    onChange={() => { }}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Session Management</div>
                                <div className="setting-description">
                                    View and manage active sessions
                                </div>
                            </div>
                            <button
                                className="btn-secondary"
                                style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                                onClick={() => toast.info('Session management coming soon')}
                            >
                                Manage Sessions
                            </button>
                        </div>

                        <div className="setting-item" style={{ gridColumn: '1 / -1' }}>
                            <div>
                                <div className="setting-label">Change Password</div>
                                <div className="setting-description">
                                    Update your account password regularly
                                </div>
                            </div>
                            <button
                                className="btn-primary"
                                style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                                onClick={() => toast.info('Password change feature coming soon')}
                            >
                                Change Password
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Notification Settings */}
            {activeTab === 'notifications' && (
                <div className="settings-section">
                    <div className="section-header">
                        <FiBell size={24} />
                        <h2>Notification Settings</h2>
                    </div>

                    <div className="settings-grid">
                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Email Notifications</div>
                                <div className="setting-description">
                                    Receive email updates about your finances
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={userProfile.notifications}
                                    onChange={(e) => handleProfileChange('notifications', e.target.checked)}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Budget Alerts</div>
                                <div className="setting-description">
                                    Get notified when approaching budget limits
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={userProfile.budgetAlerts}
                                    onChange={(e) => handleProfileChange('budgetAlerts', e.target.checked)}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Spending Limits</div>
                                <div className="setting-description">
                                    Alerts when spending exceeds daily/weekly limits
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={userProfile.spendingLimits}
                                    onChange={(e) => handleProfileChange('spendingLimits', e.target.checked)}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Bill Reminders</div>
                                <div className="setting-description">
                                    Get reminded about upcoming bill payments
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={true}
                                    onChange={() => { }}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Transaction Alerts</div>
                                <div className="setting-description">
                                    Notifications for large or unusual transactions
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={true}
                                    onChange={() => { }}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Data & Backup Settings */}
            {activeTab === 'data' && (
                <div className="settings-section">
                    <div className="section-header">
                        <FiUpload size={24} />
                        <h2>Data & Backup Settings</h2>
                    </div>

                    <div className="settings-grid">
                        <div className="setting-item">
                            <div>
                                <div className="setting-label">Automatic Backup</div>
                                <div className="setting-description">
                                    Automatically backup your data
                                </div>
                            </div>
                            <div className="switch">
                                <input
                                    type="checkbox"
                                    checked={userProfile.autoBackup}
                                    onChange={(e) => handleProfileChange('autoBackup', e.target.checked)}
                                />
                                <span className="slider"></span>
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Backup Frequency</label>
                            <select
                                value={backupFrequency}
                                onChange={(e) => setBackupFrequency(e.target.value)}
                            >
                                <option value="daily">Daily</option>
                                <option value="weekly">Weekly</option>
                                <option value="monthly">Monthly</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Data Retention Period</label>
                            <select
                                value={dataRetention}
                                onChange={(e) => setDataRetention(e.target.value)}
                            >
                                <option value="3">3 months</option>
                                <option value="6">6 months</option>
                                <option value="12">12 months</option>
                                <option value="24">24 months</option>
                                <option value="forever">Forever</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Export Format</label>
                            <select
                                value={exportFormat}
                                onChange={(e) => setExportFormat(e.target.value)}
                            >
                                <option value="csv">CSV</option>
                                <option value="json">JSON</option>
                                <option value="pdf">PDF</option>
                                <option value="excel">Excel</option>
                            </select>
                        </div>

                        <div style={{
                            gridColumn: '1 / -1',
                            display: 'flex',
                            gap: '1rem',
                            marginTop: '1rem'
                        }}>
                            <button
                                className="btn-secondary"
                                onClick={handleExportData}
                                style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', flex: 1 }}
                            >
                                <FiDownload /> Export Settings
                            </button>

                            <label className="btn-secondary" style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '0.5rem',
                                flex: 1,
                                cursor: 'pointer',
                                justifyContent: 'center'
                            }}>
                                <FiUpload /> Import Settings
                                <input
                                    type="file"
                                    accept=".json"
                                    onChange={handleImportData}
                                    style={{ display: 'none' }}
                                />
                            </label>
                        </div>

                        <div style={{
                            gridColumn: '1 / -1',
                            padding: '1rem',
                            background: 'rgba(239, 68, 68, 0.1)',
                            borderRadius: '8px',
                            border: '1px solid rgba(239, 68, 68, 0.3)'
                        }}>
                            <div style={{ fontWeight: '600', color: '#EF4444', marginBottom: '0.5rem' }}>
                                 Danger Zone
                            </div>
                            <div style={{ fontSize: '0.875rem', color: 'var(--text-color)', marginBottom: '1rem' }}>
                                Delete all your data. This action cannot be undone.
                            </div>
                            <button
                                className="btn-secondary"
                                style={{
                                    background: '#EF4444',
                                    color: 'white',
                                    border: 'none'
                                }}
                                onClick={() => toast.warning('Data deletion feature coming soon')}
                            >
                                Delete All Data
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Help & Support Section */}
            <div className="settings-section">
                <div className="section-header">
                    <h2>Help & Support</h2>
                </div>
                <div className="settings-grid">
                    <div className="setting-item">
                        <div>
                            <div className="setting-label">Documentation</div>
                            <div className="setting-description">
                                Read user guides and tutorials
                            </div>
                        </div>
                        <button
                            className="btn-secondary"
                            style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                            onClick={() => window.open('https://docs.money-manager.com', '_blank')}
                        >
                            View Docs
                        </button>
                    </div>

                    <div className="setting-item">
                        <div>
                            <div className="setting-label">Contact Support</div>
                            <div className="setting-description">
                                Get help from our support team
                            </div>
                        </div>
                        <button
                            className="btn-secondary"
                            style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                            onClick={() => window.location.href = 'mailto:support@money-manager.com'}
                        >
                            Contact Us
                        </button>
                    </div>

                    <div className="setting-item">
                        <div>
                            <div className="setting-label">Version</div>
                            <div className="setting-description">
                                Current application version
                            </div>
                        </div>
                        <div style={{ padding: '0.5rem 1rem', color: 'var(--text-light)' }}>
                            v1.0.0
                        </div>
                    </div>

                    <div className="setting-item">
                        <div>
                            <div className="setting-label">Privacy Policy</div>
                            <div className="setting-description">
                                Read our privacy policy
                            </div>
                        </div>
                        <button
                            className="btn-secondary"
                            style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                            onClick={() => toast.info('Privacy policy coming soon')}
                        >
                            View Policy
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Settings;
