import React, { useState, useEffect } from 'react';

const BudgetOverview = () => {
    const [budgets, setBudgets] = useState([
        { category: 'Food & Dining', spent: 450, limit: 600, percentage: 75, remaining: 150 },
        { category: 'Transportation', spent: 120, limit: 300, percentage: 40, remaining: 180 },
        { category: 'Shopping', spent: 270, limit: 300, percentage: 90, remaining: 30 },
        { category: 'Bills & Utilities', spent: 300, limit: 500, percentage: 60, remaining: 200 },
    ]);

    const getProgressColor = (percentage) => {
        if (percentage >= 90) return '#f44336';
        if (percentage >= 75) return '#ff9800';
        return '#4caf50';
    };

    return (
        <div className="budget-overview">
            <h3>Budget Overview</h3>
            <div className="budget-grid">
                {budgets.map((budget, index) => (
                    <div key={index} className="budget-card">
                        <div className="budget-header">
                            <h4>{budget.category}</h4>
                            <span className="budget-percentage">{budget.percentage}% spent</span>
                        </div>
                        <div className="budget-progress">
                            <div className="progress-bar">
                                <div 
                                    className="progress-fill"
                                    style={{
                                        width: `${budget.percentage}%`,
                                        backgroundColor: getProgressColor(budget.percentage)
                                    }}
                                ></div>
                            </div>
                            <div className="budget-details">
                                <span className="spent">${budget.spent} spent</span>
                                <span className="limit">${budget.limit} limit</span>
                            </div>
                            <div className="budget-remaining">
                                ${budget.remaining} remaining
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BudgetOverview;
