import React from 'react';
import { 
    FiCreditCard, FiPieChart, FiTarget, 
    FiDollarSign, FiFileText, FiSettings,
    FiHome, FiBarChart2, FiTrendingUp,
    FiBell, FiUser
} from 'react-icons/fi';

const Sidebar = () => {
    const menuItems = [
        { icon: <FiHome />, label: 'Dashboard', active: true },
        { icon: <FiCreditCard />, label: 'Transactions' },
        { icon: <FiBarChart2 />, label: 'Analytics' },
        { icon: <FiTarget />, label: 'Budget' },
        { icon: <FiTrendingUp />, label: 'Savings' },
        { icon: <FiBell />, label: 'Bills' },
        { icon: <FiSettings />, label: 'Settings' },
    ];

    return (
        <div className="sidebar">
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '12px', 
                marginBottom: '2rem',
                paddingLeft: '1rem'
            }}>
                <div style={{
                    width: '40px',
                    height: '40px',
                    background: 'linear-gradient(135deg, #4F46E5, #7C3AED)',
                    borderRadius: '10px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '1.5rem',
                    fontWeight: 'bold'
                }}>
                    ðŸ’°
                </div>
                <h2 className="sidebar-title">Money Manager</h2>
            </div>
            <nav className="sidebar-nav">
                {menuItems.map((item, index) => (
                    <a 
                        key={index} 
                        href="#" 
                        className={`nav-item ${item.active ? 'active' : ''}`}
                        onClick={(e) => {
                            e.preventDefault();
                            // Add navigation logic here
                            console.log(`Navigating to ${item.label}`);
                        }}
                    >
                        <span className="nav-icon">{item.icon}</span>
                        <span className="nav-label">{item.label}</span>
                    </a>
                ))}
            </nav>
            
            {/* User profile at bottom */}
            <div style={{
                position: 'absolute',
                bottom: '2rem',
                left: '1rem',
                right: '1rem',
                padding: '1rem',
                background: 'rgba(255, 255, 255, 0.1)',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                gap: '12px'
            }}>
                <div style={{
                    width: '40px',
                    height: '40px',
                    background: 'linear-gradient(135deg, #8B5CF6, #EC4899)',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white'
                }}>
                    <FiUser size={20} />
                </div>
                <div>
                    <div style={{ 
                        color: 'white', 
                        fontWeight: '600',
                        fontSize: '0.9rem'
                    }}>
                        John Doe
                    </div>
                    <div style={{ 
                        color: '#9CA3AF', 
                        fontSize: '0.8rem',
                        marginTop: '2px'
                    }}>
                        Premium User
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Sidebar;
