import React, { useState, useEffect } from 'react';
import { 
    LineChart, Line, XAxis, YAxis, CartesianGrid, 
    Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
import { getChartData } from '../services/api'; // You'll need to add this API function

const SpendingChart = () => {
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchChartData();
    }, []);

    const fetchChartData = async () => {
        try {
            setLoading(true);
            // You need to create this API endpoint on your backend
            const response = await getChartData();
            setChartData(response.data || []);
        } catch (err) {
            console.error('Error fetching chart data:', err);
            setError('Failed to load chart data');
            // Fallback to empty array or keep static data as backup
            setChartData([]);
        } finally {
            setLoading(false);
        }
    };

    // If you want to keep static data as fallback when API is not ready:
    const staticData = [
        { month: 'Jan', income: 5200, expenses: 3400 },
        { month: 'Feb', income: 5400, expenses: 3800 },
        { month: 'Mar', income: 5600, expenses: 3200 },
        { month: 'Apr', income: 5800, expenses: 3500 },
        { month: 'May', income: 6200, expenses: 3100 },
        { month: 'Jun', income: 6500, expenses: 4000 },
        { month: 'Jul', income: 5800, expenses: 3300 },
        { month: 'Aug', income: 6000, expenses: 3500 },
        { month: 'Sep', income: 6200, expenses: 3200 },
        { month: 'Oct', income: 5842, expenses: 3216 },
    ];

    const dataToUse = chartData.length > 0 ? chartData : staticData;

    if (loading) {
        return (
            <div className="chart-container">
                <h3>Spending Overview</h3>
                <div className="chart loading">
                    <div className="loading-spinner">Loading chart...</div>
                </div>
            </div>
        );
    }

    return (
        <div className="chart-container">
            <h3>Spending Overview</h3>
            <div className="chart">
                <ResponsiveContainer width="100%" height={300}>
                    <LineChart 
                        data={dataToUse} 
                        margin={{ top: 20, right: 30, left: 0, bottom: 5 }}
                    >
                        <CartesianGrid strokeDasharray="3 3" stroke="#eee" />
                        <XAxis 
                            dataKey="month" 
                            stroke="#666"
                            tick={{ fill: '#666' }}
                        />
                        <YAxis 
                            stroke="#666"
                            tick={{ fill: '#666' }}
                            tickFormatter={(value) => `$${value}`}
                        />
                        <Tooltip 
                            formatter={(value) => [`$${value}`, 'Amount']}
                            labelFormatter={(label) => `Month: ${label}`}
                            contentStyle={{
                                backgroundColor: 'white',
                                border: '1px solid #ddd',
                                borderRadius: '5px'
                            }}
                        />
                        <Legend />
                        <Line 
                            type="monotone" 
                            dataKey="income" 
                            stroke="#4CAF50" 
                            strokeWidth={3}
                            name="Income"
                            dot={{ stroke: '#4CAF50', strokeWidth: 2, r: 4 }}
                            activeDot={{ r: 6 }}
                        />
                        <Line 
                            type="monotone" 
                            dataKey="expenses" 
                            stroke="#F44336" 
                            strokeWidth={3}
                            name="Expenses"
                            dot={{ stroke: '#F44336', strokeWidth: 2, r: 4 }}
                            activeDot={{ r: 6 }}
                        />
                    </LineChart>
                </ResponsiveContainer>
            </div>
            {error && (
                <div className="chart-error">
                    <small>{error} (Showing sample data)</small>
                </div>
            )}
        </div>
    );
};

export default SpendingChart;
