import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Mock data for testing (remove when backend is ready)
const mockData = {
    dashboardStats: {
        totalIncome: 5842.50,
        totalExpenses: 3215.80,
        currentBalance: 12450.30,
        monthlySavings: 1250.75,
        incomeChange: '+12%',
        expenseChange: '-5%',
        savingsPercentage: '65%'
    },
    transactions: [
        {
            id: 1,
            description: 'Amazon Purchase Online Shopping',
            category: 'Shopping',
            date: '2023-10-12',
            amount: -89.99,
            type: 'EXPENSE'
        },
        {
            id: 2,
            description: 'Monthly Salary Company Inc.',
            category: 'Income',
            date: '2023-10-10',
            amount: 3500.00,
            type: 'INCOME'
        },
        {
            id: 3,
            description: 'Restaurant Dinner Italian Blanco',
            category: 'Food & Dining',
            date: '2023-10-08',
            amount: -67.50,
            type: 'EXPENSE'
        },
        {
            id: 4,
            description: 'Gas Station Fuel',
            category: 'Transportation',
            date: '2023-10-05',
            amount: -45.20,
            type: 'EXPENSE'
        },
        {
            id: 5,
            description: 'Internet Bill Monthly Subscription',
            category: 'Bills & Utilities',
            date: '2023-10-03',
            amount: -79.99,
            type: 'EXPENSE'
        }
    ],
    budgets: [
        { category: 'Food & Dining', spent: 450, limit: 600, percentage: 75, remaining: 150 },
        { category: 'Transportation', spent: 120, limit: 300, percentage: 40, remaining: 180 },
        { category: 'Shopping', spent: 270, limit: 300, percentage: 90, remaining: 30 },
        { category: 'Bills & Utilities', spent: 300, limit: 500, percentage: 60, remaining: 200 },
    ]
};

// API functions (uncomment when backend is ready)
export const getDashboardStats = async () => {
    try {
        // Uncomment when backend is ready:
        // const response = await api.get('/dashboard/stats');
        // return { data: response.data };
        
        // Mock data for now:
        return { data: mockData.dashboardStats };
    } catch (error) {
        console.error('Error fetching dashboard stats:', error);
        return { data: mockData.dashboardStats };
    }
};

export const getTransactions = async () => {
    try {
        // Uncomment when backend is ready:
        // const response = await api.get('/transactions');
        // return { data: response.data };
        
        // Mock data for now:
        return { data: mockData.transactions };
    } catch (error) {
        console.error('Error fetching transactions:', error);
        return { data: mockData.transactions };
    }
};

export const getBudgets = async () => {
    try {
        // Uncomment when backend is ready:
        // const response = await api.get('/budgets');
        // return { data: response.data };
        
        // Mock data for now:
        return { data: mockData.budgets };
    } catch (error) {
        console.error('Error fetching budgets:', error);
        return { data: mockData.budgets };
    }
};

export default api;
