import React, { useState, useEffect } from 'react';
import { getBills, addBill, toggleBillStatus, deleteBill } from '../services/api';

const Bills = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newBill, setNewBill] = useState({
        description: '',
        amount: '',
        dueDate: '',
        category: 'Bills & Utilities'
    });

    useEffect(() => {
        fetchBills();
    }, []);

    const fetchBills = async () => {
        try {
            const response = await getBills();
            setBills(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Failed to fetch bills', error);
            setLoading(false);
        }
    };

    const handleAddBill = async (e) => {
        e.preventDefault();
        try {
            // Ensure amount is a number
            const payload = {
                ...newBill,
                amount: parseFloat(newBill.amount)
            };
            await addBill(payload);
            setShowAddModal(false);
            setNewBill({ description: '', amount: '', dueDate: '', category: 'Bills & Utilities' });
            fetchBills();
        } catch (error) {
            console.error('Failed to add bill', error);
            const errorMessage = error.response?.data?.message || error.message || 'Unknown error';
            alert(`Failed to add bill: ${errorMessage}`);
        }
    };

    const handleToggleStatus = async (id) => {
        try {
            await toggleBillStatus(id);
            fetchBills();
        } catch (error) {
            console.error('Failed to toggle bill status', error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this bill?')) {
            try {
                await deleteBill(id);
                fetchBills();
            } catch (error) {
                console.error('Failed to delete bill', error);
            }
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: 'numeric' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    const getDaysDue = (dueDate) => {
        const today = new Date();
        const due = new Date(dueDate);
        const diffTime = due - today;
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays < 0) return { text: `Overdue by ${Math.abs(diffDays)} days`, class: 'overdue' };
        if (diffDays === 0) return { text: 'Due today', class: 'due-soon' };
        if (diffDays <= 3) return { text: `Due in ${diffDays} days`, class: 'due-soon' };
        return { text: `Due in ${diffDays} days`, class: '' };
    };

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <div className="header-content">
                    <h1>Bills</h1>
                    <p>Manage and track your recurring bills</p>
                </div>
                <button
                    className="btn-primary"
                    onClick={() => setShowAddModal(true)}
                >
                    + Add Bill
                </button>
            </div>

            {showAddModal && (
                <div className="modal-overlay" style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(0, 0, 0, 0.5)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 2000,
                    backdropFilter: 'blur(4px)'
                }}>
                    <div className="modal-content">
                        <h3>Add New Bill</h3>
                        <form onSubmit={handleAddBill}>
                            <div className="form-group">
                                <label>Description</label>
                                <input
                                    type="text"
                                    value={newBill.description}
                                    onChange={(e) => setNewBill({ ...newBill, description: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Amount</label>
                                <input
                                    type="number"
                                    value={newBill.amount}
                                    onChange={(e) => setNewBill({ ...newBill, amount: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Due Date</label>
                                <input
                                    type="date"
                                    value={newBill.dueDate}
                                    onChange={(e) => setNewBill({ ...newBill, dueDate: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Category</label>
                                <select
                                    value={newBill.category}
                                    onChange={(e) => setNewBill({ ...newBill, category: e.target.value })}
                                >
                                    <option>Bills & Utilities</option>
                                    <option>Rent</option>
                                    <option>Subscription</option>
                                    <option>Insurance</option>
                                    <option>Loan</option>
                                    <option>Other</option>
                                </select>
                            </div>
                            <div className="modal-actions">
                                <button type="button" onClick={() => setShowAddModal(false)}>Cancel</button>
                                <button type="submit" className="submit-btn">Add Bill</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            <div className="bills-list-container">
                {loading ? (
                    <p>Loading bills...</p>
                ) : bills.length === 0 ? (
                    <div style={{ textAlign: 'center', color: 'var(--text-secondary)' }}>
                        <p>No bills found. Add one to get started!</p>
                    </div>
                ) : (
                    <div className="bills-grid">
                        {bills.map(bill => {
                            const dueStatus = getDaysDue(bill.dueDate);
                            return (
                                <div key={bill.id} className={`bill-item ${bill.isPaid ? 'paid' : ''}`}>
                                    <div className="bill-info">
                                        <h3>{bill.description}</h3>
                                        <div className="bill-meta">
                                            <span>{bill.category}</span>
                                            <span>•</span>
                                            <span className={`bill-status ${!bill.isPaid ? dueStatus.class : ''}`}>
                                                {formatDate(bill.dueDate)} ({dueStatus.text})
                                            </span>
                                        </div>
                                    </div>
                                    <div className="bill-actions">
                                        <div className="bill-amount">
                                            ${bill.amount.toFixed(2)}
                                        </div>
                                        <button
                                            className={`btn-mark-paid ${bill.isPaid ? 'paid' : ''}`}
                                            onClick={() => handleToggleStatus(bill.id)}
                                        >
                                            {bill.isPaid ? 'Paid' : 'Mark Paid'}
                                        </button>
                                        <button
                                            className="btn-delete"
                                            onClick={() => handleDelete(bill.id)}
                                            title="Delete Bill"
                                        >
                                            ×
                                        </button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Bills;
