// Update the chart styling in your existing SpendingChart.jsx
// In the LineChart component, update the styling:

<LineChart 
    data={data} 
    margin={{ top: 20, right: 30, left: 20, bottom: 10 }}
>
    <CartesianGrid 
        strokeDasharray="3 3" 
        stroke="#E5E7EB" 
        vertical={false}
        strokeOpacity={0.5}
    />
    <XAxis 
        dataKey="month" 
        stroke="#6B7280"
        tick={{ fill: '#6B7280', fontSize: 12, fontWeight: 500 }}
        axisLine={false}
        tickLine={false}
        tickMargin={10}
    />
    <YAxis 
        stroke="#6B7280"
        tick={{ fill: '#6B7280', fontSize: 12, fontWeight: 500 }}
        axisLine={false}
        tickLine={false}
        tickFormatter={(value) => `$${value.toLocaleString()}`}
        tickMargin={10}
    />
    <Tooltip 
        formatter={(value) => [`$${value.toLocaleString()}`, '']}
        labelFormatter={(label) => `Month: ${label}`}
        contentStyle={{
            backgroundColor: 'white',
            border: '1px solid #E5E7EB',
            borderRadius: '10px',
            padding: '12px 16px',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.12)',
            fontSize: '14px'
        }}
        labelStyle={{ 
            color: '#1F2937', 
            fontWeight: 600,
            fontSize: '14px',
            marginBottom: '8px'
        }}
        itemStyle={{ 
            color: '#1F2937',
            fontSize: '14px',
            padding: '4px 0'
        }}
    />
    <Legend 
        verticalAlign="top"
        height={36}
        iconType="circle"
        iconSize={10}
        wrapperStyle={{
            paddingBottom: '20px'
        }}
    />
    <Line 
        type="monotone" 
        dataKey="income" 
        stroke="#4F46E5" 
        strokeWidth={3}
        name="Income"
        dot={{ stroke: '#4F46E5', strokeWidth: 2, r: 4 }}
        activeDot={{ r: 7, strokeWidth: 2 }}
    />
    <Line 
        type="monotone" 
        dataKey="expenses" 
        stroke="#10B981" 
        strokeWidth={3}
        name="Expenses"
        dot={{ stroke: '#10B981', strokeWidth: 2, r: 4 }}
        activeDot={{ r: 7, strokeWidth: 2 }}
    />
</LineChart>
