import React, { useState } from 'react';
import { FiSun, FiMoon } from 'react-icons/fi'; // Add these imports
import FinancialCards from './FinancialCards';
import SpendingChart from './SpendingChart';
import TransactionsTable from './TransactionsTable';
import BudgetOverview from './BudgetOverview';
import QuickActions from './QuickActions';

const Dashboard = ({ isDarkMode, toggleTheme, refreshKey, setRefreshKey }) => {
    const handleTransactionAdded = () => {
        setRefreshKey(prev => prev + 1);
    };

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <div className="header-content">
                    <h1>Financial Dashboard</h1>
                    <p>Welcome back! Here's your financial overview.</p>
                </div>
                <div className="header-actions">
                    {/* Theme Toggle Button */}
                    <button 
                        className="btn-secondary"
                        onClick={toggleTheme}
                        style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                    >
                        {isDarkMode ? (
                            <>
                                <FiSun /> Light Mode
                            </>
                        ) : (
                            <>
                                <FiMoon /> Dark Mode
                            </>
                        )}
                    </button>
                </div>
            </div>

            {/* Financial Cards Section */}
            <FinancialCards key={refreshKey} />

            {/* Main Content Layout */}
            <div className="dashboard-content">
                {/* Left Column with Chart and Transactions */}
                <div className="left-column">
                    <div className="chart-section">
                        <SpendingChart key={refreshKey} />
                    </div>
                    <div className="transactions-section">
                        <TransactionsTable key={refreshKey} />
                    </div>
                </div>

                {/* Right Column with Budget and Quick Actions */}
                <div className="right-column">
                    <div className="budget-section">
                        <BudgetOverview key={refreshKey} />
                    </div>
                    <div className="quick-actions-section">
                        <QuickActions onTransactionAdded={handleTransactionAdded} />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard; // Make sure this is at the bottom
