                                        <YAxis 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                            tickFormatter={(value) => `$${value}`}
                                        />
                                        <Tooltip 
                                            formatter={(value) => [`$${value.toLocaleString()}`, 'Amount']}
                                            labelStyle={{ color: 'var(--text-color)', fontWeight: '600' }}
                                            contentStyle={{
                                                backgroundColor: 'var(--card-bg)',
                                                border: '1px solid var(--border-color)',
                                                borderRadius: '8px'
                                            }}
                                        />
                                        <Legend />
                                        <Area 
                                            type="monotone" 
                                            dataKey="income" 
                                            stroke="#10B981" 
                                            fillOpacity={1}
                                            fill="url(#colorIncome)"
                                            name="Income"
                                            strokeWidth={2}
                                        />
                                        <Area 
                                            type="monotone" 
                                            dataKey="expenses" 
                                            stroke="#EF4444" 
                                            fillOpacity={1}
                                            fill="url(#colorExpenses)"
                                            name="Expenses"
                                            strokeWidth={2}
                                        />
                                    </AreaChart>
                                </ResponsiveContainer>
                            </div>
                        </ChartContainer>

                        {/* Spending by Category */}
                        <ChartContainer 
                            title="Spending by Category"
                            isExpanded={expandedChart === 'category'}
                            onExpand={() => toggleChartExpansion('category')}
                        >
                            <div style={{ height: expandedChart === 'category' ? '400px' : '300px' }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <PieChart>
                                        <Pie
                                            data={categoryData}
                                            cx="50%"
                                            cy="50%"
                                            labelLine={false}
                                            label={(entry) => `${entry.name}: $${entry.value.toLocaleString()}`}
                                            outerRadius={expandedChart === 'category' ? 120 : 80}
                                            fill="#8884d8"
                                            dataKey="value"
                                            paddingAngle={2}
                                        >
                                            {categoryData.map((entry, index) => (
                                                <Cell 
                                                    key={`cell-${index}`} 
                                                    fill={COLORS[index % COLORS.length]} 
                                                />
                                            ))}
                                        </Pie>
                                        <Tooltip 
                                            formatter={(value, name, props) => [
                                                `$${value.toLocaleString()}`,
                                                `${props.payload.name} (${((value / metrics.totalExpenses) * 100).toFixed(1)}%)`
                                            ]}
                                            contentStyle={{
                                                backgroundColor: 'var(--card-bg)',
                                                border: '1px solid var(--border-color)',
                                                borderRadius: '8px'
                                            }}
                                        />
                                    </PieChart>
                                </ResponsiveContainer>
                            </div>
                        </ChartContainer>

                        {/* Daily Spending Trend */}
                        <ChartContainer 
                            title="Daily Spending Trend"
                            isExpanded={expandedChart === 'daily'}
                            onExpand={() => toggleChartExpansion('daily')}
                        >
                            <div style={{ height: expandedChart === 'daily' ? '350px' : '250px' }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart data={dailyData}>
                                        <CartesianGrid strokeDasharray="3 3" stroke="var(--border-color)" vertical={false} />
                                        <XAxis 
                                            dataKey="date" 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)', fontSize: 12 }}
                                        />
                                        <YAxis 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                            tickFormatter={(value) => `$${value}`}
                                        />
                                        <Tooltip 
                                            formatter={(value, name, props) => [
                                                `$${value.toLocaleString()}`,
                                                `${props.payload.date} (${props.payload.transactions} transactions)`
                                            ]}
                                            contentStyle={{
                                                backgroundColor: 'var(--card-bg)',
                                                border: '1px solid var(--border-color)',
                                                borderRadius: '8px'
                                            }}
                                        />
                                        <Bar 
                                            dataKey="amount" 
                                            fill="#4F46E5" 
                                            radius={[4, 4, 0, 0]}
                                            name="Daily Spending"
                                        />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>
                        </ChartContainer>

                        {/* Income Sources */}
                        <ChartContainer 
                            title="Income Sources"
                            isExpanded={expandedChart === 'incomeSources'}
                            onExpand={() => toggleChartExpansion('incomeSources')}
                        >
                            <div style={{ height: expandedChart === 'incomeSources' ? '350px' : '250px' }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <RadarChart data={incomeSources}>
                                        <PolarGrid stroke="var(--border-color)" />
                                        <PolarAngleAxis 
                                            dataKey="name" 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                        />
                                        <PolarRadiusAxis 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                            tickFormatter={(value) => `$${value}`}
                                        />
                                        <Radar
                                            name="Income Sources"
                                            dataKey="value"
                                            stroke="#10B981"
                                            fill="#10B981"
                                            fillOpacity={0.6}
                                            strokeWidth={2}
                                        />
                                        <Tooltip 
                                            formatter={(value) => [`$${value.toLocaleString()}`, 'Amount']}
                                            contentStyle={{
                                                backgroundColor: 'var(--card-bg)',
                                                border: '1px solid var(--border-color)',
                                                borderRadius: '8px'
                                            }}
                                        />
                                        <Legend />
                                    </RadarChart>
                                </ResponsiveContainer>
                            </div>
                        </ChartContainer>
                    </div>

                    {/* Bottom Section - Spending Patterns & Top Expenses */}
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: '1fr 1fr',
                        gap: '2rem'
                    }}>
                        {/* Weekly Spending Pattern */}
                        <div style={{
                            background: 'var(--card-bg)',
                            borderRadius: '16px',
                            padding: '1.75rem',
                            border: '1px solid var(--border-color)'
                        }}>
                            <h3 style={{ 
                                fontSize: '1.25rem', 
                                fontWeight: '700', 
                                color: 'var(--text-color)',
                                margin: '0 0 1.5rem 0',
                                display: 'flex',
                                alignItems: 'center',
                                gap: '0.5rem'
                            }}>
                                <FiCalendar /> Weekly Spending Pattern
                            </h3>
                            <div style={{ height: '250px' }}>
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart 
                                        data={Object.entries(spendingPatterns.weekday).map(([day, amount]) => ({ day, amount }))}
                                        margin={{ top: 20, right: 30, left: 0, bottom: 5 }}
                                    >
                                        <CartesianGrid strokeDasharray="3 3" stroke="var(--border-color)" vertical={false} />
                                        <XAxis 
                                            dataKey="day" 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                        />
                                        <YAxis 
                                            stroke="var(--text-light)"
                                            tick={{ fill: 'var(--text-light)' }}
                                            tickFormatter={(value) => `$${value}`}
                                        />
                                        <Tooltip 
                                            formatter={(value) => [`$${value.toLocaleString()}`, 'Amount']}
                                            contentStyle={{
                                                backgroundColor: 'var(--card-bg)',
                                                border: '1px solid var(--border-color)',
                                                borderRadius: '8px'
                                            }}
                                        />
                                        <Bar 
                                            dataKey="amount" 
                                            fill="#F59E0B" 
                                            radius={[4, 4, 0, 0]}
                                            name="Spending by Day"
                                        />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>
                        </div>

                        {/* Top Expenses */}
                        <div style={{
                            background: 'var(--card-bg)',
                            borderRadius: '16px',
                            padding: '1.75rem',
                            border: '1px solid var(--border-color)',
                            overflow: 'hidden'
                        }}>
                            <div style={{ 
                                display: 'flex', 
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                marginBottom: '1.5rem'
                            }}>
                                <h3 style={{ 
                                    fontSize: '1.25rem', 
                                    fontWeight: '700', 
                                    color: 'var(--text-color)',
                                    margin: 0,
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.5rem'
                                }}>
                                    <FiFilter /> Top 5 Expenses
                                </h3>
                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                    {topExpenses.length} transactions
                                </div>
                            </div>
                            <div style={{ 
                                display: 'flex', 
                                flexDirection: 'column', 
                                gap: '0.75rem',
                                maxHeight: '250px',
                                overflowY: 'auto',
                                paddingRight: '0.5rem'
                            }}>
                                {topExpenses.map((expense, index) => (
                                    <div key={expense.id} style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        justifyContent: 'space-between',
                                        padding: '1rem',
                                        background: 'var(--light-color)',
                                        borderRadius: '10px',
                                        border: '1px solid var(--border-color)',
                                        transition: 'all 0.2s'
                                    }}
                                    onMouseEnter={(e) => e.currentTarget.style.transform = 'translateX(4px)'}
                                    onMouseLeave={(e) => e.currentTarget.style.transform = 'translateX(0)'}
                                    >
                                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', flex: 1 }}>
                                            <div style={{
                                                width: '40px',
                                                height: '40px',
                                                borderRadius: '10px',
                                                background: COLORS[index % COLORS.length] + '20',
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                color: COLORS[index % COLORS.length],
                                                fontWeight: '700',
                                                fontSize: '1rem'
                                            }}>
                                                #{index + 1}
                                            </div>
                                            <div style={{ flex: 1 }}>
                                                <div style={{ 
                                                    fontSize: '0.95rem', 
                                                    fontWeight: '600', 
                                                    color: 'var(--text-color)',
                                                    marginBottom: '0.125rem',
                                                    lineHeight: 1.2
                                                }}>
                                                    {expense.description.length > 40 
                                                        ? expense.description.substring(0, 40) + '...' 
                                                        : expense.description}
                                                </div>
                                                <div style={{ 
                                                    fontSize: '0.8rem', 
                                                    color: 'var(--text-light)',
                                                    display: 'flex',
                                                    alignItems: 'center',
                                                    gap: '0.5rem'
                                                }}>
                                                    <span style={{ 
                                                        padding: '0.125rem 0.5rem',
                                                        background: COLORS[index % COLORS.length] + '20',
                                                        borderRadius: '4px',
                                                        color: COLORS[index % COLORS.length],
                                                        fontWeight: '500'
                                                    }}>
                                                        {expense.category}
                                                    </span>
                                                    <span>
                                                        {new Date(expense.date).toLocaleDateString('en-US', { 
                                                            month: 'short', 
                                                            day: 'numeric' 
                                                        })}
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ 
                                            fontSize: '1.1rem', 
                                            fontWeight: '700', 
                                            color: '#EF4444',
                                            textAlign: 'right'
                                        }}>
                                            -${expense.amount.toLocaleString()}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Insights Summary */}
                    <div style={{
                        background: 'var(--card-bg)',
                        borderRadius: '16px',
                        padding: '1.75rem',
                        border: '1px solid var(--border-color)',
                        marginTop: '2rem'
                    }}>
                        <h3 style={{ 
                            fontSize: '1.25rem', 
                            fontWeight: '700', 
                            color: 'var(--text-color)',
                            margin: '0 0 1rem 0'
                        }}>
                            Key Insights
                        </h3>
                        <div style={{
                            display: 'grid',
                            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                            gap: '1rem'
                        }}>
                            <div style={{
                                padding: '1rem',
                                background: 'rgba(16, 185, 129, 0.1)',
                                borderRadius: '10px',
                                borderLeft: '4px solid #10B981'
                            }}>
                                <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#10B981', marginBottom: '0.25rem' }}>
                                     Positive Trend
                                </div>
                                <div style={{ fontSize: '0.9rem', color: 'var(--text-color)' }}>
                                    Your income grew by <strong>{metrics.incomeGrowth.toFixed(1)}%</strong> compared to last month.
                                </div>
                            </div>
                            <div style={{
                                padding: '1rem',
                                background: 'rgba(239, 68, 68, 0.1)',
                                borderRadius: '10px',
                                borderLeft: '4px solid #EF4444'
                            }}>
                                <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#EF4444', marginBottom: '0.25rem' }}>
                                     Watch Out
                                </div>
                                <div style={{ fontSize: '0.9rem', color: 'var(--text-color)' }}>
                                    Expenses increased by <strong>{Math.abs(metrics.expenseGrowth).toFixed(1)}%</strong>. Consider reviewing your budget.
                                </div>
                            </div>
                            <div style={{
                                padding: '1rem',
                                background: 'rgba(79, 70, 229, 0.1)',
                                borderRadius: '10px',
                                borderLeft: '4px solid #4F46E5'
                            }}>
                                <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#4F46E5', marginBottom: '0.25rem' }}>
                                     Recommendation
                                </div>
                                <div style={{ fontSize: '0.9rem', color: 'var(--text-color)' }}>
                                    Your savings rate is <strong>{metrics.savingsRate.toFixed(1)}%</strong>. Aim for 20% to meet long-term goals.
                                </div>
                            </div>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default Analytics;
