import React, { useState, useEffect } from 'react';
import { 
    LineChart, Line, XAxis, YAxis, CartesianGrid, 
    Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
// First, add this import to api.js (if not already there):
// export const getSpendingChartData = async () => {
//     const response = await api.get('/dashboard/spending-chart');
//     return { data: response.data };
// };

// For now, let's use getTransactions and calculate the data
import { getTransactions } from '../services/api';

const SpendingChart = () => {
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // This will re-run when the component mounts or when parent triggers refresh
    const fetchChartData = async () => {
        try {
            setLoading(true);
            setError(null);
            
            // Get all transactions
            const response = await getTransactions();
            const transactions = response.data;
            
            if (!transactions || transactions.length === 0) {
                // If no transactions, use static data
                setChartData(getStaticData());
                return;
            }
            
            // Group transactions by month
            const monthlyData = {};
            
            transactions.forEach(transaction => {
                const date = new Date(transaction.date);
                const monthKey = date.toLocaleString('default', { month: 'short' });
                
                if (!monthlyData[monthKey]) {
                    monthlyData[monthKey] = {
                        month: monthKey,
                        income: 0,
                        expenses: 0
                    };
                }
                
                if (transaction.type === 'INCOME') {
                    monthlyData[monthKey].income += transaction.amount;
                } else if (transaction.type === 'EXPENSE') {
                    monthlyData[monthKey].expenses += Math.abs(transaction.amount);
                }
            });
            
            // Convert to array and sort by month order
            const chartArray = Object.values(monthlyData);
            sortChartData(chartArray);
            
            setChartData(chartArray.length > 0 ? chartArray : getStaticData());
            
        } catch (err) {
            console.error('Error fetching chart data:', err);
            setError('Failed to load chart data');
            // Fallback to static data
            setChartData(getStaticData());
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchChartData();
    }, []); // This will run when component mounts

    // Helper function to sort data by month
    const sortChartData = (data) => {
        const monthOrder = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        data.sort((a, b) => monthOrder.indexOf(a.month) - monthOrder.indexOf(b.month));
    };

    // Static data fallback
    const getStaticData = () => {
        return [
            { month: 'Jan', income: 5200, expenses: 3400 },
            { month: 'Feb', income: 5400, expenses: 3800 },
            { month: 'Mar', income: 5600, expenses: 3200 },
            { month: 'Apr', income: 5800, expenses: 3500 },
            { month: 'May', income: 6200, expenses: 3100 },
            { month: 'Jun', income: 6500, expenses: 4000 },
            { month: 'Jul', income: 5800, expenses: 3300 },
            { month: 'Aug', income: 6000, expenses: 3500 },
            { month: 'Sep', income: 6200, expenses: 3200 },
            { month: 'Oct', income: 5842, expenses: 3216 },
        ];
    };

    if (loading) {
        return (
            <div className="chart-container">
                <h3>Spending Overview</h3>
                <div className="chart loading" style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                    <div>Loading chart data...</div>
                </div>
            </div>
        );
    }

    return (
        <div className="chart-container">
            <h3>Spending Overview</h3>
            {error && (
                <div style={{ 
                    color: '#f44336', 
                    backgroundColor: '#ffebee', 
                    padding: '8px', 
                    borderRadius: '4px',
                    marginBottom: '10px',
                    fontSize: '0.9rem'
                }}>
                    {error} (showing sample data)
                </div>
            )}
            <div className="chart">
                <ResponsiveContainer width="100%" height={300}>
                    <LineChart 
                        data={chartData} 
                        margin={{ top: 20, right: 30, left: 0, bottom: 5 }}
                    >
                        <CartesianGrid strokeDasharray="3 3" stroke="#eee" />
                        <XAxis 
                            dataKey="month" 
                            stroke="#666"
                            tick={{ fill: '#666' }}
                        />
                        <YAxis 
                            stroke="#666"
                            tick={{ fill: '#666' }}
                            tickFormatter={(value) => `$${value}`}
                        />
                        <Tooltip 
                            formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']}
                            labelFormatter={(label) => `Month: ${label}`}
                            contentStyle={{
                                backgroundColor: 'white',
                                border: '1px solid #ddd',
                                borderRadius: '5px'
                            }}
                        />
                        <Legend />
                        <Line 
                            type="monotone" 
                            dataKey="income" 
                            stroke="#4CAF50" 
                            strokeWidth={3}
                            name="Income"
                            dot={{ stroke: '#4CAF50', strokeWidth: 2, r: 4 }}
                            activeDot={{ r: 6 }}
                        />
                        <Line 
                            type="monotone" 
                            dataKey="expenses" 
                            stroke="#F44336" 
                            strokeWidth={3}
                            name="Expenses"
                            dot={{ stroke: '#F44336', strokeWidth: 2, r: 4 }}
                            activeDot={{ r: 6 }}
                        />
                    </LineChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

export default SpendingChart;
