import React, { useState, useEffect } from 'react';
import { getTransactions } from '../services/api';

const TransactionsTable = () => {
    const [transactions, setTransactions] = useState([]);

    useEffect(() => {
        fetchTransactions();
    }, []);

    const fetchTransactions = async () => {
        try {
            const response = await getTransactions();
            setTransactions(response.data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
            // Sample data if API fails
            setTransactions([
                {
                    id: 1,
                    description: 'Amazon Purchase Online Shopping',
                    category: 'Shopping',
                    date: '2023-10-12',
                    amount: -89.99,
                    type: 'EXPENSE'
                },
                {
                    id: 2,
                    description: 'Monthly Salary Company Inc.',
                    category: 'Income',
                    date: '2023-10-10',
                    amount: 3500.00,
                    type: 'INCOME'
                },
                {
                    id: 3,
                    description: 'Restaurant Dinner Italian Blanco',
                    category: 'Food & Dining',
                    date: '2023-10-08',
                    amount: -67.50,
                    type: 'EXPENSE'
                },
                {
                    id: 4,
                    description: 'Gas Station Fuel',
                    category: 'Transportation',
                    date: '2023-10-05',
                    amount: -45.20,
                    type: 'EXPENSE'
                },
                {
                    id: 5,
                    description: 'Internet Bill Monthly Subscription',
                    category: 'Bills & Utilities',
                    date: '2023-10-03',
                    amount: -79.99,
                    type: 'EXPENSE'
                }
            ]);
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: 'numeric' };
        return new Date(dateString).toLocaleDateString('en-US', options);
    };

    const formatAmount = (amount) => {
        return amount >= 0 ? `+$${amount.toFixed(2)}` : `-$${Math.abs(amount).toFixed(2)}`;
    };

    return (
        <div className="transactions-container">
            <h3>Recent Transactions</h3>
            <table className="transactions-table">
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Category</th>
                        <th>Date</th>
                        <th>Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction.id}>
                            <td>{transaction.description}</td>
                            <td>
                                <span className={`category-badge ${transaction.category.toLowerCase().replace(' & ', '-')}`}>
                                    {transaction.category}
                                </span>
                            </td>
                            <td>{formatDate(transaction.date)}</td>
                            <td className={`amount ${transaction.amount >= 0 ? 'positive' : 'negative'}`}>
                                {formatAmount(transaction.amount)}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TransactionsTable;
