import React from 'react';
import { 
    FiCreditCard, FiPieChart, FiTarget, 
    FiDollarSign, FiFileText, FiSettings,
    FiHome, FiBarChart2, FiTrendingUp,
    FiBell, FiUser
} from 'react-icons/fi';

const Sidebar = () => {
    const menuItems = [
        { icon: <FiHome />, label: 'Dashboard', active: true },
        { icon: <FiCreditCard />, label: 'Transactions' },
        { icon: <FiBarChart2 />, label: 'Analytics' },
        { icon: <FiTarget />, label: 'Budget' },
        { icon: <FiTrendingUp />, label: 'Savings' },
        { icon: <FiBell />, label: 'Bills' },
        { icon: <FiSettings />, label: 'Settings' },
    ];

    return (
        <div className="sidebar">
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '16px', 
                marginBottom: '2rem',
                paddingLeft: '1rem'
            }}>
                <div style={{
                    width: '48px',
                    height: '48px',
                    background: 'linear-gradient(135deg, #4F46E5, #7C3AED)',
                    borderRadius: '12px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '1.8rem',
                    fontWeight: 'bold',
                    boxShadow: '0 4px 12px rgba(79, 70, 229, 0.3)'
                }}>
                    ðŸ’°
                </div>
                <div>
                    <h1 style={{
                        fontSize: '1.5rem',
                        fontWeight: '800',
                        background: 'linear-gradient(135deg, #fff, #a5b4fc)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent',
                        margin: 0,
                        lineHeight: 1.2
                    }}>
                        Money Manager
                    </h1>
                    <p style={{
                        fontSize: '0.75rem',
                        color: '#a5b4fc',
                        margin: '4px 0 0 0',
                        opacity: 0.8,
                        fontWeight: '500'
                    }}>
                        Your Financial Dashboard
                    </p>
                </div>
            </div>
            <nav className="sidebar-nav">
                {menuItems.map((item, index) => (
                    <a 
                        key={index} 
                        href="#" 
                        className={`nav-item ${item.active ? 'active' : ''}`}
                        onClick={(e) => {
                            e.preventDefault();
                            console.log(`Navigating to ${item.label}`);
                        }}
                    >
                        <span className="nav-icon">{item.icon}</span>
                        <span className="nav-label">{item.label}</span>
                    </a>
                ))}
            </nav>
            
            {/* Rest of the sidebar remains the same */}
        </div>
    );
};

export default Sidebar;
