import React, { useState, useEffect } from 'react';
import { FiUser, FiBell, FiLock, FiGlobe, FiMoon, FiSave, FiEye, FiEyeOff, FiX, FiMail, FiSmartphone, FiCreditCard, FiDatabase, FiDownload, FiUpload } from 'react-icons/fi';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Settings = () => {
    // User Profile State
    const [userProfile, setUserProfile] = useState({
        name: 'Dulan Madhuka',
        email: 'Dulanmadhuka@gmail.com',
        phone: '07122226666',
        currency: 'LKR',
        language: 'English',
        timezone: 'Asia/Colombo'
    });

    // Notification Preferences
    const [notifications, setNotifications] = useState({
        emailNotifications: true,
        pushNotifications: true,
        transactionAlerts: true,
        budgetWarnings: true,
        weeklyReports: true,
        marketingEmails: false,
        billReminders: true,
        savingsGoalUpdates: true
    });

    // Privacy & Security
    const [privacy, setPrivacy] = useState({
        showAmounts: true,
        biometricLogin: false,
        autoLogout: 15, // minutes
        dataSharing: false,
        twoFactorAuth: false,
        hideBalance: false
    });

    // Display Preferences
    const [display, setDisplay] = useState({
        theme: 'light',
        compactMode: false,
        showAnimations: true,
        fontSize: 'medium',
        currencySymbol: true,
        decimalPlaces: 2,
        dateFormat: 'MM/DD/YYYY'
    });

    // Password Change
    const [passwords, setPasswords] = useState({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [showPassword, setShowPassword] = useState({
        current: false,
        new: false,
        confirm: false
    });

    // Data Management
    const [dataManagement, setDataManagement] = useState({
        autoBackup: true,
        backupFrequency: 'weekly',
        cloudSync: false,
        dataRetention: '6months'
    });

    // Loading state
    const [loading, setLoading] = useState(false);
    const [activeTab, setActiveTab] = useState('profile');

    // Initialize from localStorage
    useEffect(() => {
        const loadSettings = () => {
            try {
                // Load user profile
                const savedProfile = localStorage.getItem('userProfile');
                if (savedProfile) {
                    setUserProfile(JSON.parse(savedProfile));
                }

                // Load notifications
                const savedNotifications = localStorage.getItem('notificationSettings');
                if (savedNotifications) {
                    setNotifications(JSON.parse(savedNotifications));
                }

                // Load privacy settings
                const savedPrivacy = localStorage.getItem('privacySettings');
                if (savedPrivacy) {
                    setPrivacy(JSON.parse(savedPrivacy));
                }

                // Load display settings
                const savedDisplay = localStorage.getItem('displaySettings');
                if (savedDisplay) {
                    setDisplay(JSON.parse(savedDisplay));
                }

                // Load data management
                const savedData = localStorage.getItem('dataSettings');
                if (savedData) {
                    setDataManagement(JSON.parse(savedData));
                }
            } catch (error) {
                console.error('Error loading settings:', error);
            }
        };

        loadSettings();
    }, []);

    // Apply theme changes
    useEffect(() => {
        if (display.theme === 'dark') {
            document.body.classList.add('dark-mode');
            localStorage.setItem('darkMode', 'true');
        } else {
            document.body.classList.remove('dark-mode');
            localStorage.setItem('darkMode', 'false');
        }
    }, [display.theme]);

    // Handle profile updates
    const handleProfileChange = (e) => {
        const { name, value } = e.target;
        setUserProfile(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Handle notification toggles
    const handleNotificationToggle = (key) => {
        setNotifications(prev => ({
            ...prev,
            [key]: !prev[key]
        }));
    };

    // Handle privacy toggles
    const handlePrivacyToggle = (key) => {
        setPrivacy(prev => ({
            ...prev,
            [key]: !prev[key]
        }));
    };

    // Handle display changes
    const handleDisplayChange = (key, value) => {
        setDisplay(prev => ({
            ...prev,
            [key]: value
        }));
    };

    // Handle data management changes
    const handleDataChange = (key, value) => {
        setDataManagement(prev => ({
            ...prev,
            [key]: value
        }));
    };

    // Handle password change
    const handlePasswordChange = (e) => {
        const { name, value } = e.target;
        setPasswords(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Toggle password visibility
    const togglePasswordVisibility = (field) => {
        setShowPassword(prev => ({
            ...prev,
            [field]: !prev[field]
        }));
    };

    // Save settings
    const saveSettings = async (section) => {
        setLoading(true);

        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 800));

            // Save to localStorage based on section
            switch (section) {
                case 'profile':
                    localStorage.setItem('userProfile', JSON.stringify(userProfile));
                    break;
                case 'notifications':
                    localStorage.setItem('notificationSettings', JSON.stringify(notifications));
                    break;
                case 'privacy':
                    localStorage.setItem('privacySettings', JSON.stringify(privacy));
                    break;
                case 'display':
                    localStorage.setItem('displaySettings', JSON.stringify(display));
                    break;
                case 'data':
                    localStorage.setItem('dataSettings', JSON.stringify(dataManagement));
                    break;
                case 'password':
                    if (passwords.newPassword !== passwords.confirmPassword) {
                        throw new Error('New passwords do not match');
                    }
                    if (passwords.newPassword.length < 8) {
                        throw new Error('Password must be at least 8 characters');
                    }
                    if (!/[A-Z]/.test(passwords.newPassword)) {
                        throw new Error('Password must contain at least one uppercase letter');
                    }
                    if (!/[0-9]/.test(passwords.newPassword)) {
                        throw new Error('Password must contain at least one number');
                    }

                    localStorage.setItem('passwordChanged', 'true');
                    setPasswords({ currentPassword: '', newPassword: '', confirmPassword: '' });
                    break;
                default:
                    // Save all
                    localStorage.setItem('userProfile', JSON.stringify(userProfile));
                    localStorage.setItem('notificationSettings', JSON.stringify(notifications));
                    localStorage.setItem('privacySettings', JSON.stringify(privacy));
                    localStorage.setItem('displaySettings', JSON.stringify(display));
                    localStorage.setItem('dataSettings', JSON.stringify(dataManagement));
            }

            toast.success(`${section === 'all' ? 'All' : section.charAt(0).toUpperCase() + section.slice(1)} settings saved successfully!`, {
                position: "top-right",
                autoClose: 3000,
            });

        } catch (error) {
            toast.error(`Error: ${error.message}`, {
                position: "top-right",
                autoClose: 5000,
            });
        } finally {
            setLoading(false);
        }
    };

    // Export all data
    const exportData = () => {
        try {
            const allData = {
                userProfile,
                notifications,
                privacy,
                display,
                dataManagement,
                exportedAt: new Date().toISOString(),
                appVersion: '1.0.0'
            };

            const dataStr = JSON.stringify(allData, null, 2);
            const dataBlob = new Blob([dataStr], { type: 'application/json' });
            const url = URL.createObjectURL(dataBlob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `money-manager-settings-${new Date().toISOString().split('T')[0]}.json`;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url);

            toast.success('Settings exported successfully!', {
                position: "top-right",
                autoClose: 3000,
            });

        } catch (error) {
            toast.error('Failed to export settings', {
                position: "top-right",
                autoClose: 5000,
            });
        }
    };

    // Import settings
    const importSettings = () => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = '.json';

        input.onchange = (e) => {
            const file = e.target.files[0];
            const reader = new FileReader();

            reader.onload = (event) => {
                try {
                    const importedData = JSON.parse(event.target.result);

                    // Validate imported data
                    if (importedData.userProfile) setUserProfile(importedData.userProfile);
                    if (importedData.notifications) setNotifications(importedData.notifications);
                    if (importedData.privacy) setPrivacy(importedData.privacy);
                    if (importedData.display) setDisplay(importedData.display);
                    if (importedData.dataManagement) setDataManagement(importedData.dataManagement);

                    toast.success('Settings imported successfully!', {
                        position: "top-right",
                        autoClose: 3000,
                    });

                    // Save imported settings
                    saveSettings('all');

                } catch (error) {
                    toast.error('Invalid settings file', {
                        position: "top-right",
                        autoClose: 5000,
                    });
                }
            };

            reader.readAsText(file);
        };

        input.click();
    };

    // Reset to defaults
    const resetToDefaults = (section) => {
        if (window.confirm(`Are you sure you want to reset ${section} settings to defaults? This action cannot be undone.`)) {
            switch (section) {
                case 'profile':
                    setUserProfile({
                        name: 'Dulan Madhuka',
                        email: 'Dulanmadhuka@gmail.com',
                        phone: '07122226666',
                        currency: 'LKR',
                        language: 'English',
                        timezone: 'Asia/Colombo'
                    });
                    break;
                case 'notifications':
                    setNotifications({
                        emailNotifications: true,
                        pushNotifications: true,
                        transactionAlerts: true,
                        budgetWarnings: true,
                        weeklyReports: true,
                        marketingEmails: false,
                        billReminders: true,
                        savingsGoalUpdates: true
                    });
                    break;
                case 'privacy':
                    setPrivacy({
                        showAmounts: true,
                        biometricLogin: false,
                        autoLogout: 15,
                        dataSharing: false,
                        twoFactorAuth: false,
                        hideBalance: false
                    });
                    break;
                case 'display':
                    setDisplay({
                        theme: 'light',
                        compactMode: false,
                        showAnimations: true,
                        fontSize: 'medium',
                        currencySymbol: true,
                        decimalPlaces: 2,
                        dateFormat: 'MM/DD/YYYY'
                    });
                    break;
                case 'data':
                    setDataManagement({
                        autoBackup: true,
                        backupFrequency: 'weekly',
                        cloudSync: false,
                        dataRetention: '6months'
                    });
                    break;
                case 'all':
                    resetToDefaults('profile');
                    resetToDefaults('notifications');
                    resetToDefaults('privacy');
                    resetToDefaults('display');
                    resetToDefaults('data');
                    break;
                default:
                    break;
            }

            toast.info(`${section} settings reset to defaults`, {
                position: "top-right",
                autoClose: 3000,
            });
        }
    };

    // Tabs configuration
    const tabs = [
        { id: 'profile', label: 'Profile', icon: <FiUser /> },
        { id: 'notifications', label: 'Notifications', icon: <FiBell /> },
        { id: 'privacy', label: 'Privacy & Security', icon: <FiLock /> },
        { id: 'display', label: 'Display', icon: <FiMoon /> },
        { id: 'data', label: 'Data Management', icon: <FiDatabase /> }
    ];

    // Password strength checker
    const checkPasswordStrength = (password) => {
        let strength = 0;
        if (password.length >= 8) strength++;
        if (/[A-Z]/.test(password)) strength++;
        if (/[0-9]/.test(password)) strength++;
        if (/[^A-Za-z0-9]/.test(password)) strength++;

        return strength;
    };

    const getPasswordStrengthColor = (strength) => {
        if (strength === 0) return '#EF4444';
        if (strength === 1) return '#F59E0B';
        if (strength === 2) return '#3B82F6';
        if (strength === 3) return '#10B981';
        return '#059669';
    };

    const passwordStrength = checkPasswordStrength(passwords.newPassword);

    return (
        <div className="dashboard">
            <ToastContainer />

            <div className="dashboard-header">
                <div className="header-content">
                    <h1>Settings</h1>
                    <p>Customize your Money Manager experience</p>
                </div>
                <div className="header-actions">
                    <button
                        className="btn-secondary"
                        onClick={() => resetToDefaults('all')}
                        style={{ gap: '0.5rem' }}
                    >
                        <FiX /> Reset All
                    </button>
                    <button
                        className="btn-primary"
                        onClick={() => saveSettings('all')}
                        disabled={loading}
                        style={{ gap: '0.5rem' }}
                    >
                        {loading ? (
                            <>
                                <div className="spinner"></div>
                                Saving...
                            </>
                        ) : (
                            <>
                                <FiSave /> Save All Changes
                            </>
                        )}
                    </button>
                </div>
            </div>

            {/* Settings Tabs */}
            <div style={{
                display: 'flex',
                gap: '1rem',
                marginBottom: '2rem',
                borderBottom: '1px solid var(--border-color)',
                paddingBottom: '1rem',
                overflowX: 'auto'
            }}>
                {tabs.map((tab) => (
                    <button
                        key={tab.id}
                        onClick={() => setActiveTab(tab.id)}
                        style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.5rem',
                            padding: '0.75rem 1.5rem',
                            background: activeTab === tab.id ? 'var(--primary-color)' : 'transparent',
                            color: activeTab === tab.id ? 'white' : 'var(--text-color)',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            fontWeight: '600',
                            fontSize: '0.95rem',
                            whiteSpace: 'nowrap',
                            transition: 'all 0.2s ease'
                        }}
                    >
                        {tab.icon}
                        {tab.label}
                    </button>
                ))}
            </div>

            {/* Tab Content */}
            <div style={{ background: 'var(--card-bg)', borderRadius: '12px', padding: '2rem', border: '1px solid var(--border-color)' }}>
                {/* Profile Tab */}
                {activeTab === 'profile' && (
                    <div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '2rem' }}>
                            <div style={{
                                width: '60px',
                                height: '60px',
                                borderRadius: '50%',
                                background: 'linear-gradient(135deg, #4F46E5, #7C3AED)',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: 'white',
                                fontSize: '1.5rem',
                                fontWeight: 'bold'
                            }}>
                                {userProfile.name.charAt(0)}
                            </div>
                            <div>
                                <h3 style={{ fontSize: '1.5rem', fontWeight: '700', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                    {userProfile.name}
                                </h3>
                                <p style={{ color: 'var(--text-light)', fontSize: '0.875rem' }}>
                                    {userProfile.email}
                                </p>
                            </div>
                        </div>

                        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
                            <div className="form-group">
                                <label>Full Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={userProfile.name}
                                    onChange={handleProfileChange}
                                    placeholder="Enter your name"
                                />
                            </div>
                            <div className="form-group">
                                <label>Email Address</label>
                                <input
                                    type="email"
                                    name="email"
                                    value={userProfile.email}
                                    onChange={handleProfileChange}
                                    placeholder="Enter your email"
                                />
                            </div>
                            <div className="form-group">
                                <label>Phone Number</label>
                                <input
                                    type="tel"
                                    name="phone"
                                    value={userProfile.phone}
                                    onChange={handleProfileChange}
                                    placeholder="Enter phone number"
                                />
                            </div>
                            <div className="form-group">
                                <label>Preferred Currency</label>
                                <select
                                    name="currency"
                                    value={userProfile.currency}
                                    onChange={handleProfileChange}
                                >
                                    <option value="USD">USD ($)</option>
                                    <option value="LKR">LKR (Rs)</option>
                                    <option value="EUR">EUR ()</option>
                                    <option value="GBP">GBP ()</option>
                                    <option value="JPY">JPY ()</option>
                                    <option value="CAD">CAD ($)</option>
                                    <option value="AUD">AUD ($)</option>
                                    <option value="INR">INR ()</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Language</label>
                                <select
                                    name="language"
                                    value={userProfile.language}
                                    onChange={handleProfileChange}
                                >
                                    <option value="English">English</option>
                                    <option value="Sinhala">Sinhala</option>
                                    <option value="Spanish">Spanish</option>
                                    <option value="French">French</option>
                                    <option value="German">German</option>
                                    <option value="Chinese">Chinese</option>
                                    <option value="Japanese">Japanese</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Timezone</label>
                                <select
                                    name="timezone"
                                    value={userProfile.timezone}
                                    onChange={handleProfileChange}
                                >
                                    <option value="Asia/Colombo">Asia/Colombo (Sri Lanka)</option>
                                    <option value="UTC">UTC</option>
                                    <option value="America/New_York">America/New_York (EST)</option>
                                    <option value="Europe/London">Europe/London (GMT)</option>
                                    <option value="Asia/Tokyo">Asia/Tokyo (JST)</option>
                                    <option value="Australia/Sydney">Australia/Sydney (AEST)</option>
                                </select>
                            </div>
                        </div>

                        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem', paddingTop: '1.5rem', borderTop: '1px solid var(--border-color)' }}>
                            <button
                                className="btn-secondary"
                                onClick={() => resetToDefaults('profile')}
                            >
                                Reset Profile
                            </button>
                            <button
                                className="btn-primary"
                                onClick={() => saveSettings('profile')}
                                disabled={loading}
                            >
                                Save Profile
                            </button>
                        </div>
                    </div>
                )}

                {/* Notifications Tab */}
                {activeTab === 'notifications' && (
                    <div>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-color)', marginBottom: '1.5rem' }}>
                            Notification Preferences
                        </h3>

                        <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginBottom: '2rem' }}>
                            {Object.entries(notifications).map(([key, value]) => (
                                <div key={key} style={{
                                    display: 'flex',
                                    justifyContent: 'space-between',
                                    alignItems: 'center',
                                    padding: '1rem',
                                    background: 'var(--light-color)',
                                    borderRadius: '8px',
                                    border: '1px solid var(--border-color)'
                                }}>
                                    <div>
                                        <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                            {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                                        </div>
                                        <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                            {key.includes('email') ? 'Receive notifications via email' :
                                                key.includes('push') ? 'Receive push notifications in browser' :
                                                    key.includes('transaction') ? 'Get alerts for new transactions' :
                                                        key.includes('budget') ? 'Warnings when approaching budget limits' :
                                                            key.includes('weekly') ? 'Weekly financial summary reports' :
                                                                key.includes('marketing') ? 'Marketing and promotional emails' :
                                                                    key.includes('bill') ? 'Reminders for upcoming bill payments' :
                                                                        'Updates on your savings goals progress'}
                                        </div>
                                    </div>
                                    <label className="switch">
                                        <input
                                            type="checkbox"
                                            checked={value}
                                            onChange={() => handleNotificationToggle(key)}
                                        />
                                        <span className="slider"></span>
                                    </label>
                                </div>
                            ))}
                        </div>

                        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem', paddingTop: '1.5rem', borderTop: '1px solid var(--border-color)' }}>
                            <button
                                className="btn-secondary"
                                onClick={() => resetToDefaults('notifications')}
                            >
                                Reset Notifications
                            </button>
                            <button
                                className="btn-primary"
                                onClick={() => saveSettings('notifications')}
                                disabled={loading}
                            >
                                Save Notifications
                            </button>
                        </div>
                    </div>
                )}

                {/* Privacy & Security Tab */}
                {activeTab === 'privacy' && (
                    <div>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-color)', marginBottom: '1.5rem' }}>
                            Privacy & Security Settings
                        </h3>

                        <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem', marginBottom: '2rem' }}>
                            {/* Privacy Settings */}
                            <div>
                                <h4 style={{ fontSize: '1rem', fontWeight: '600', color: 'var(--text-color)', marginBottom: '1rem' }}>
                                    Privacy Settings
                                </h4>
                                {Object.entries(privacy).map(([key, value]) => (
                                    key === 'autoLogout' ? (
                                        <div key={key} style={{
                                            display: 'flex',
                                            justifyContent: 'space-between',
                                            alignItems: 'center',
                                            padding: '1rem',
                                            background: 'var(--light-color)',
                                            borderRadius: '8px',
                                            border: '1px solid var(--border-color)',
                                            marginBottom: '1rem'
                                        }}>
                                            <div>
                                                <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>Auto Logout Timer</div>
                                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                                    Automatically log out after {value} minutes of inactivity
                                                </div>
                                            </div>
                                            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                                                <input
                                                    type="range"
                                                    min="1"
                                                    max="60"
                                                    value={value}
                                                    onChange={(e) => setPrivacy(prev => ({ ...prev, autoLogout: parseInt(e.target.value) }))}
                                                    style={{ width: '150px' }}
                                                />
                                                <span style={{ fontWeight: '600', minWidth: '30px' }}>{value}m</span>
                                            </div>
                                        </div>
                                    ) : (
                                        <div key={key} style={{
                                            display: 'flex',
                                            justifyContent: 'space-between',
                                            alignItems: 'center',
                                            padding: '1rem',
                                            background: 'var(--light-color)',
                                            borderRadius: '8px',
                                            border: '1px solid var(--border-color)',
                                            marginBottom: '1rem'
                                        }}>
                                            <div>
                                                <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                                    {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                                                </div>
                                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                                    {key === 'showAmounts' ? 'Display transaction amounts in dashboard' :
                                                        key === 'biometricLogin' ? 'Use fingerprint or face ID for login' :
                                                            key === 'dataSharing' ? 'Share anonymous usage data to improve app' :
                                                                key === 'twoFactorAuth' ? 'Enable two-factor authentication for extra security' :
                                                                    'Hide account balance from dashboard'}
                                                </div>
                                            </div>
                                            <label className="switch">
                                                <input
                                                    type="checkbox"
                                                    checked={value}
                                                    onChange={() => handlePrivacyToggle(key)}
                                                />
                                                <span className="slider"></span>
                                            </label>
                                        </div>
                                    )
                                ))}
                            </div>

                            {/* Password Change */}
                            <div>
                                <h4 style={{ fontSize: '1rem', fontWeight: '600', color: 'var(--text-color)', marginBottom: '1rem' }}>
                                    Change Password
                                </h4>
                                <div style={{
                                    display: 'flex',
                                    flexDirection: 'column',
                                    gap: '1rem',
                                    padding: '1rem',
                                    background: 'var(--light-color)',
                                    borderRadius: '8px',
                                    border: '1px solid var(--border-color)'
                                }}>
                                    <div className="form-group">
                                        <label>Current Password</label>
                                        <div style={{ position: 'relative' }}>
                                            <input
                                                type={showPassword.current ? 'text' : 'password'}
                                                name="currentPassword"
                                                value={passwords.currentPassword}
                                                onChange={handlePasswordChange}
                                                placeholder="Enter current password"
                                                style={{ paddingRight: '40px', width: '100%' }}
                                            />
                                            <button
                                                type="button"
                                                style={{
                                                    position: 'absolute',
                                                    right: '10px',
                                                    top: '50%',
                                                    transform: 'translateY(-50%)',
                                                    background: 'none',
                                                    border: 'none',
                                                    color: 'var(--text-light)',
                                                    cursor: 'pointer',
                                                    padding: '4px'
                                                }}
                                                onClick={() => togglePasswordVisibility('current')}
                                            >
                                                {showPassword.current ? <FiEyeOff /> : <FiEye />}
                                            </button>
                                        </div>
                                    </div>

                                    <div className="form-group">
                                        <label>New Password</label>
                                        <div style={{ position: 'relative' }}>
                                            <input
                                                type={showPassword.new ? 'text' : 'password'}
                                                name="newPassword"
                                                value={passwords.newPassword}
                                                onChange={handlePasswordChange}
                                                placeholder="Enter new password"
                                                style={{ paddingRight: '40px', width: '100%' }}
                                            />
                                            <button
                                                type="button"
                                                style={{
                                                    position: 'absolute',
                                                    right: '10px',
                                                    top: '50%',
                                                    transform: 'translateY(-50%)',
                                                    background: 'none',
                                                    border: 'none',
                                                    color: 'var(--text-light)',
                                                    cursor: 'pointer',
                                                    padding: '4px'
                                                }}
                                                onClick={() => togglePasswordVisibility('new')}
                                            >
                                                {showPassword.new ? <FiEyeOff /> : <FiEye />}
                                            </button>
                                        </div>
                                        {passwords.newPassword && (
                                            <div style={{ marginTop: '0.5rem' }}>
                                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.25rem' }}>
                                                    <span style={{ fontSize: '0.75rem', color: 'var(--text-light)' }}>Password Strength:</span>
                                                    <span style={{ fontSize: '0.75rem', color: getPasswordStrengthColor(passwordStrength) }}>
                                                        {passwordStrength === 0 ? 'Very Weak' :
                                                            passwordStrength === 1 ? 'Weak' :
                                                                passwordStrength === 2 ? 'Fair' :
                                                                    passwordStrength === 3 ? 'Good' : 'Strong'}
                                                    </span>
                                                </div>
                                                <div style={{
                                                    height: '4px',
                                                    background: 'var(--border-color)',
                                                    borderRadius: '2px',
                                                    overflow: 'hidden'
                                                }}>
                                                    <div style={{
                                                        height: '100%',
                                                        width: `${passwordStrength * 25}%`,
                                                        background: getPasswordStrengthColor(passwordStrength),
                                                        transition: 'width 0.3s ease'
                                                    }} />
                                                </div>
                                                <div style={{ fontSize: '0.75rem', color: 'var(--text-light)', marginTop: '0.5rem' }}>
                                                    Password must be at least 8 characters with uppercase letters and numbers
                                                </div>
                                            </div>
                                        )}
                                    </div>

                                    <div className="form-group">
                                        <label>Confirm New Password</label>
                                        <div style={{ position: 'relative' }}>
                                            <input
                                                type={showPassword.confirm ? 'text' : 'password'}
                                                name="confirmPassword"
                                                value={passwords.confirmPassword}
                                                onChange={handlePasswordChange}
                                                placeholder="Confirm new password"
                                                style={{ paddingRight: '40px', width: '100%' }}
                                            />
                                            <button
                                                type="button"
                                                style={{
                                                    position: 'absolute',
                                                    right: '10px',
                                                    top: '50%',
                                                    transform: 'translateY(-50%)',
                                                    background: 'none',
                                                    border: 'none',
                                                    color: 'var(--text-light)',
                                                    cursor: 'pointer',
                                                    padding: '4px'
                                                }}
                                                onClick={() => togglePasswordVisibility('confirm')}
                                            >
                                                {showPassword.confirm ? <FiEyeOff /> : <FiEye />}
                                            </button>
                                        </div>
                                        {passwords.confirmPassword && passwords.newPassword !== passwords.confirmPassword && (
                                            <div style={{ fontSize: '0.75rem', color: '#EF4444', marginTop: '0.25rem' }}>
                                                Passwords do not match
                                            </div>
                                        )}
                                    </div>

                                    <button
                                        className="btn-primary"
                                        onClick={() => saveSettings('password')}
                                        disabled={loading || !passwords.currentPassword || !passwords.newPassword || !passwords.confirmPassword || passwords.newPassword !== passwords.confirmPassword}
                                        style={{ marginTop: '0.5rem' }}
                                    >
                                        Update Password
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem', paddingTop: '1.5rem', borderTop: '1px solid var(--border-color)' }}>
                            <button
                                className="btn-secondary"
                                onClick={() => resetToDefaults('privacy')}
                            >
                                Reset Privacy
                            </button>
                            <button
                                className="btn-primary"
                                onClick={() => saveSettings('privacy')}
                                disabled={loading}
                            >
                                Save Privacy
                            </button>
                        </div>
                    </div>
                )}

                {/* Display Tab */}
                {activeTab === 'display' && (
                    <div>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-color)', marginBottom: '1.5rem' }}>
                            Display Preferences
                        </h3>

                        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
                            <div className="form-group">
                                <label>Theme</label>
                                <div style={{ display: 'flex', gap: '1rem', marginTop: '0.5rem' }}>
                                    {['light', 'dark', 'system'].map((theme) => (
                                        <label key={theme} style={{ cursor: 'pointer', flex: 1 }}>
                                            <input
                                                type="radio"
                                                name="theme"
                                                value={theme}
                                                checked={display.theme === theme}
                                                onChange={() => handleDisplayChange('theme', theme)}
                                                style={{ display: 'none' }}
                                            />
                                            <div style={{
                                                display: 'flex',
                                                flexDirection: 'column',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                padding: '1rem',
                                                borderRadius: '8px',
                                                fontWeight: '600',
                                                border: '2px solid var(--border-color)',
                                                background: 'var(--card-bg)',
                                                transition: 'all 0.3s ease',
                                                borderColor: display.theme === theme ? 'var(--primary-color)' : 'var(--border-color)',
                                                background: display.theme === theme ? 'var(--primary-color)' : 'var(--card-bg)',
                                                color: display.theme === theme ? 'white' : 'var(--text-color)'
                                            }}>
                                                {theme.charAt(0).toUpperCase() + theme.slice(1)}
                                            </div>
                                        </label>
                                    ))}
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Font Size</label>
                                <div style={{ display: 'flex', gap: '1rem', marginTop: '0.5rem' }}>
                                    {['small', 'medium', 'large'].map((size) => (
                                        <label key={size} style={{ cursor: 'pointer', flex: 1 }}>
                                            <input
                                                type="radio"
                                                name="fontSize"
                                                value={size}
                                                checked={display.fontSize === size}
                                                onChange={() => handleDisplayChange('fontSize', size)}
                                                style={{ display: 'none' }}
                                            />
                                            <div style={{
                                                display: 'flex',
                                                flexDirection: 'column',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                padding: '1rem',
                                                borderRadius: '8px',
                                                fontWeight: '600',
                                                border: '2px solid var(--border-color)',
                                                background: 'var(--card-bg)',
                                                transition: 'all 0.3s ease',
                                                borderColor: display.fontSize === size ? 'var(--primary-color)' : 'var(--border-color)',
                                                background: display.fontSize === size ? 'var(--primary-color)' : 'var(--card-bg)',
                                                color: display.fontSize === size ? 'white' : 'var(--text-color)'
                                            }}>
                                                {size.charAt(0).toUpperCase() + size.slice(1)}
                                                <div style={{
                                                    marginTop: '0.5rem',
                                                    fontWeight: '700',
                                                    fontSize: size === 'small' ? '12px' : size === 'medium' ? '14px' : '16px'
                                                }}>
                                                    Aa
                                                </div>
                                            </div>
                                        </label>
                                    ))}
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Date Format</label>
                                <select
                                    value={display.dateFormat}
                                    onChange={(e) => handleDisplayChange('dateFormat', e.target.value)}
                                >
                                    <option value="MM/DD/YYYY">MM/DD/YYYY</option>
                                    <option value="DD/MM/YYYY">DD/MM/YYYY</option>
                                    <option value="YYYY-MM-DD">YYYY-MM-DD</option>
                                    <option value="DD MMM YYYY">DD MMM YYYY</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Decimal Places</label>
                                <select
                                    value={display.decimalPlaces}
                                    onChange={(e) => handleDisplayChange('decimalPlaces', parseInt(e.target.value))}
                                >
                                    <option value={0}>0 ($100)</option>
                                    <option value={1}>1 ($100.0)</option>
                                    <option value={2}>2 ($100.00)</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Show Currency Symbol</label>
                                <label className="switch">
                                    <input
                                        type="checkbox"
                                        checked={display.currencySymbol}
                                        onChange={() => handleDisplayChange('currencySymbol', !display.currencySymbol)}
                                    />
                                    <span className="slider"></span>
                                </label>
                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)', marginTop: '0.5rem' }}>
                                    Display currency symbol ($, Rs, ) with amounts
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Compact Mode</label>
                                <label className="switch">
                                    <input
                                        type="checkbox"
                                        checked={display.compactMode}
                                        onChange={() => handleDisplayChange('compactMode', !display.compactMode)}
                                    />
                                    <span className="slider"></span>
                                </label>
                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)', marginTop: '0.5rem' }}>
                                    Reduce padding and spacing for more content
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Show Animations</label>
                                <label className="switch">
                                    <input
                                        type="checkbox"
                                        checked={display.showAnimations}
                                        onChange={() => handleDisplayChange('showAnimations', !display.showAnimations)}
                                    />
                                    <span className="slider"></span>
                                </label>
                                <div style={{ fontSize: '0.875rem', color: 'var(--text-light)', marginTop: '0.5rem' }}>
                                    Enable UI animations and transitions
                                </div>
                            </div>
                        </div>

                        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem', paddingTop: '1.5rem', borderTop: '1px solid var(--border-color)' }}>
                            <button
                                className="btn-secondary"
                                onClick={() => resetToDefaults('display')}
                            >
                                Reset Display
                            </button>
                            <button
                                className="btn-primary"
                                onClick={() => saveSettings('display')}
                                disabled={loading}
                            >
                                Save Display
                            </button>
                        </div>
                    </div>
                )}

                {/* Data Management Tab */}
                {activeTab === 'data' && (
                    <div>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-color)', marginBottom: '1.5rem' }}>
                            Data Management
                        </h3>

                        <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem', marginBottom: '2rem' }}>
                            {/* Auto Backup */}
                            <div style={{
                                padding: '1.5rem',
                                background: 'var(--light-color)',
                                borderRadius: '8px',
                                border: '1px solid var(--border-color)'
                            }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                                    <div>
                                        <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                            Auto Backup
                                        </div>
                                        <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                            Automatically backup your data
                                        </div>
                                    </div>
                                    <label className="switch">
                                        <input
                                            type="checkbox"
                                            checked={dataManagement.autoBackup}
                                            onChange={() => handleDataChange('autoBackup', !dataManagement.autoBackup)}
                                        />
                                        <span className="slider"></span>
                                    </label>
                                </div>

                                {dataManagement.autoBackup && (
                                    <div className="form-group">
                                        <label>Backup Frequency</label>
                                        <select
                                            value={dataManagement.backupFrequency}
                                            onChange={(e) => handleDataChange('backupFrequency', e.target.value)}
                                        >
                                            <option value="daily">Daily</option>
                                            <option value="weekly">Weekly</option>
                                            <option value="monthly">Monthly</option>
                                        </select>
                                    </div>
                                )}
                            </div>

                            {/* Cloud Sync */}
                            <div style={{
                                padding: '1.5rem',
                                background: 'var(--light-color)',
                                borderRadius: '8px',
                                border: '1px solid var(--border-color)'
                            }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                                    <div>
                                        <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                            Cloud Sync
                                        </div>
                                        <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                            Sync your data across devices
                                        </div>
                                    </div>
                                    <label className="switch">
                                        <input
                                            type="checkbox"
                                            checked={dataManagement.cloudSync}
                                            onChange={() => handleDataChange('cloudSync', !dataManagement.cloudSync)}
                                        />
                                        <span className="slider"></span>
                                    </label>
                                </div>
                            </div>

                            {/* Data Retention */}
                            <div style={{
                                padding: '1.5rem',
                                background: 'var(--light-color)',
                                borderRadius: '8px',
                                border: '1px solid var(--border-color)'
                            }}>
                                <div style={{ marginBottom: '1rem' }}>
                                    <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                        Data Retention
                                    </div>
                                    <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                        How long to keep your transaction history
                                    </div>
                                </div>

                                <div className="form-group">
                                    <select
                                        value={dataManagement.dataRetention}
                                        onChange={(e) => handleDataChange('dataRetention', e.target.value)}
                                    >
                                        <option value="3months">3 Months</option>
                                        <option value="6months">6 Months</option>
                                        <option value="1year">1 Year</option>
                                        <option value="2years">2 Years</option>
                                        <option value="forever">Forever</option>
                                    </select>
                                </div>
                            </div>

                            {/* Import/Export */}
                            <div style={{
                                padding: '1.5rem',
                                background: 'var(--light-color)',
                                borderRadius: '8px',
                                border: '1px solid var(--border-color)'
                            }}>
                                <div style={{ marginBottom: '1rem' }}>
                                    <div style={{ fontWeight: '600', color: 'var(--text-color)', marginBottom: '0.25rem' }}>
                                        Import & Export Data
                                    </div>
                                    <div style={{ fontSize: '0.875rem', color: 'var(--text-light)' }}>
                                        Backup or restore your data
                                    </div>
                                </div>

                                <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
                                    <button
                                        className="btn-secondary"
                                        onClick={exportData}
                                        style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                                    >
                                        <FiDownload /> Export All Data
                                    </button>
                                    <button
                                        className="btn-secondary"
                                        onClick={importSettings}
                                        style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
                                    >
                                        <FiUpload /> Import Settings
                                    </button>
                                </div>
                            </div>

                            {/* Clear Data */}
                            <div style={{
                                padding: '1.5rem',
                                background: 'rgba(239, 68, 68, 0.1)',
                                borderRadius: '8px',
                                border: '1px solid rgba(239, 68, 68, 0.2)'
                            }}>
                                <div style={{ marginBottom: '1rem' }}>
                                    <div style={{ fontWeight: '600', color: '#EF4444', marginBottom: '0.25rem' }}>
                                        Clear All Data
                                    </div>
                                    <div style={{ fontSize: '0.875rem', color: '#EF4444' }}>
                                        Warning: This will delete all your data and cannot be undone
                                    </div>
                                </div>

                                <button
                                    className="btn-secondary"
                                    onClick={() => {
                                        if (window.confirm('Are you sure you want to delete ALL data? This action cannot be undone.')) {
                                            localStorage.clear();
                                            toast.success('All data cleared successfully', {
                                                position: "top-right",
                                                autoClose: 3000,
                                            });
                                            window.location.reload();
                                        }
                                    }}
                                    style={{
                                        background: 'rgba(239, 68, 68, 0.1)',
                                        borderColor: '#EF4444',
                                        color: '#EF4444'
                                    }}
                                >
                                    Clear All Data
                                </button>
                            </div>
                        </div>

                        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '1rem', paddingTop: '1.5rem', borderTop: '1px solid var(--border-color)' }}>
                            <button
                                className="btn-secondary"
                                onClick={() => resetToDefaults('data')}
                            >
                                Reset Data Settings
                            </button>
                            <button
                                className="btn-primary"
                                onClick={() => saveSettings('data')}
                                disabled={loading}
                            >
                                Save Data Settings
                            </button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Settings;
