import React, { useState, useEffect } from 'react';
import { FiX } from 'react-icons/fi';
import { addTransaction } from '../services/api';
import './AddTransactionModal.css'; // Import the CSS

const AddTransactionModal = ({ isOpen, onClose, defaultType = 'EXPENSE', onTransactionAdded }) => {
    const [formData, setFormData] = useState({
        description: '',
        category: '',
        date: new Date().toISOString().split('T')[0],
        amount: '',
        type: defaultType
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const categories = {
        'INCOME': ['Salary', 'Freelance', 'Business', 'Investment', 'Other Income'],
        'EXPENSE': [
            'Shopping',
            'Food & Dining',
            'Transportation',
            'Bills & Utilities',
            'Entertainment',
            'Healthcare',
            'Education',
            'Rent/Mortgage',
            'Other'
        ]
    };

    useEffect(() => {
        if (isOpen) {
            const initialCategory = defaultType === 'INCOME' ? 'Salary' : 'Shopping';
            setFormData({
                description: '',
                category: initialCategory,
                date: new Date().toISOString().split('T')[0],
                amount: '',
                type: defaultType || 'EXPENSE'
            });
            setError('');
        }
    }, [isOpen, defaultType]);

    if (!isOpen) return null;

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleTypeChange = (type) => {
        setFormData(prev => ({
            ...prev,
            type: type,
            category: type === 'INCOME' ? 'Salary' : 'Shopping'
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            if (!formData.description.trim()) {
                throw new Error('Description is required');
            }
            if (!formData.amount || parseFloat(formData.amount) <= 0) {
                throw new Error('Amount must be greater than 0');
            }

            const transactionData = {
                description: formData.description.trim(),
                category: formData.category,
                date: formData.date,
                amount: parseFloat(formData.amount),
                type: formData.type
            };

            await addTransaction(transactionData);

            setFormData({
                description: '',
                category: formData.type === 'INCOME' ? 'Salary' : 'Shopping',
                date: new Date().toISOString().split('T')[0],
                amount: '',
                type: formData.type
            });

            if (onTransactionAdded) {
                onTransactionAdded();
            }

            onClose();

        } catch (err) {
            setError(err.response?.data?.message || err.message || 'Failed to add transaction');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content add-transaction-modal" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>Add Transaction</h2>
                    <p className="modal-subtitle">Record a new income or expense transaction</p>
                    <button className="close-btn" onClick={onClose}>
                        <FiX size={20} />
                    </button>
                </div>

                {error && (
                    <div className="error-message">
                        {error}
                    </div>
                )}

                <form onSubmit={handleSubmit}>
                    {/* Transaction Type */}
                    <div className="form-group">
                        <label className="section-label">Transaction Type</label>
                        <div className="transaction-type-toggle">
                            <button
                                type="button"
                                className={`type-toggle-btn ${formData.type === 'INCOME' ? 'active income' : ''}`}
                                onClick={() => handleTypeChange('INCOME')}
                            >
                                Income
                            </button>
                            <button
                                type="button"
                                className={`type-toggle-btn ${formData.type === 'EXPENSE' ? 'active expense' : ''}`}
                                onClick={() => handleTypeChange('EXPENSE')}
                            >
                                Expense
                            </button>
                        </div>
                    </div>

                    {/* Description */}
                    <div className="form-group">
                        <label className="section-label">Description</label>
                        <input
                            type="text"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            placeholder="Enter transaction description"
                            required
                            autoFocus
                        />
                    </div>

                    {/* Category */}
                    <div className="form-group">
                        <label className="section-label">Category</label>
                        <select
                            name="category"
                            value={formData.category}
                            onChange={handleChange}
                            required
                        >
                            <option value="" disabled>Select a category</option>
                            {(categories[formData.type] || categories.EXPENSE).map(category => (
                                <option key={category} value={category}>
                                    {category}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Date and Amount */}
                    <div className="form-row">
                        <div className="form-group">
                            <label className="section-label">Date</label>
                            <input
                                type="date"
                                name="date"
                                value={formData.date}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label className="section-label">Amount</label>
                            <div className="amount-input-container">
                                <span className="currency-symbol">$</span>
                                <input
                                    type="number"
                                    name="amount"
                                    value={formData.amount}
                                    onChange={handleChange}
                                    placeholder="0.00"
                                    step="0.01"
                                    min="0.01"
                                    required
                                    className="amount-input"
                                />
                            </div>
                        </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="modal-actions">
                        <button
                            type="button"
                            className="cancel-btn"
                            onClick={onClose}
                            disabled={loading}
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            className="add-btn"
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="spinner"></span>
                                    Adding...
                                </>
                            ) : (
                                'Add Transaction'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddTransactionModal;
